/* ---------------------------------------- */
/*         DESIGN TOKENS & SETUP          */
/* ---------------------------------------- */
:root {
  /* Palette */
  --km-color-primary: #0f766e;
  --km-color-accent: #94a3b8;
  --km-color-accent-bold: #34d399;
  --km-color-neutral-light: #F8FAFC;
  --km-color-neutral-dark: #111827;
  --km-color-white: #FFFFFF;
  --km-color-black: #000000;
  --km-color-text: #334155;
  --km-color-border: #e2e8f0;

  /* Typography */
  --km-font-family-headings: 'Plus Jakarta Sans', sans-serif;
  --km-font-family-body: 'Plus Jakarta Sans', sans-serif;
  --km-fs-h1: clamp(2.5rem, 5.5vw, 4.5rem);
  --km-fs-h2: clamp(2rem, 4vw, 3rem);
  --km-fs-h3: clamp(1.5rem, 3vw, 2rem);
  --km-fs-h4: clamp(1.2rem, 2.5vw, 1.5rem);
  --km-fs-body: clamp(1rem, 2vw, 1.125rem);
  --km-fs-small: 0.875rem;
  --km-lh-headings: 1.2;
  --km-lh-body: 1.7;
  --km-fw-headings: 700;
  --km-fw-body: 400;
  --km-fw-bold: 600;

  /* Spacing */
  --km-spacing-section-y: clamp(4rem, 8vw, 6rem);
  --km-spacing-grid-gap: clamp(1rem, 2.5vw, 2rem);

  /* Radius & Shadows */
  --km-border-radius-cards: 16px;
  --km-border-radius-buttons: 9999px;
  --km-shadow-subtle: 0 4px 6px -1px rgba(0, 0, 0, 0.05), 0 2px 4px -2px rgba(0, 0, 0, 0.05);
  --km-shadow-medium: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1);
}

/* ---------------------------------------- */
/*              GLOBAL STYLES             */
/* ---------------------------------------- */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  scroll-behavior: smooth;
  scroll-padding-top: 80px; /* Offset for sticky header */
}

body {
  font-family: var(--km-font-family-body);
  font-size: var(--km-fs-body);
  line-height: var(--km-lh-body);
  color: var(--km-color-text);
  background-color: var(--km-color-white);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

h1, h2, h3, h4 {
  font-family: var(--km-font-family-headings);
  font-weight: var(--km-fw-headings);
  line-height: var(--km-lh-headings);
  color: var(--km-color-neutral-dark);
}

h1 { font-size: var(--km-fs-h1); }
h2 { font-size: var(--km-fs-h2); }
h3 { font-size: var(--km-fs-h3); }
h4 { font-size: var(--km-fs-h4); }

img, svg {
  max-width: 100%;
  height: auto;
  display: block;
}

ul {
  list-style: none;
}

a {
    text-decoration: none;
    color: var(--km-color-primary);
    transition: color 0.2s ease;
}

a:hover {
    color: var(--km-color-neutral-dark);
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* ---------------------------------------- */
/*           LAYOUT & CONTAINERS          */
/* ---------------------------------------- */
.km-container {
  width: 100%;
  max-width: 1200px;
  margin-left: auto;
  margin-right: auto;
  padding-left: 1.5rem;
  padding-right: 1.5rem;
}

.km-container--narrow {
    max-width: 800px;
}

.km-section {
  padding-top: var(--km-spacing-section-y);
  padding-bottom: var(--km-spacing-section-y);
}

.km-section--light {
    background-color: var(--km-color-neutral-light);
}

.km-section__header {
  text-align: center;
  max-width: 700px;
  margin: 0 auto 3rem;
}

.km-section__header h2 {
    margin-bottom: 0.75rem;
}

.km-grid {
  display: grid;
  gap: var(--km-spacing-grid-gap);
}

.km-grid--2-col {
  grid-template-columns: 1fr;
}

.km-grid--3-col {
  grid-template-columns: 1fr;
}

@media (min-width: 768px) {
  .km-grid--2-col {
    grid-template-columns: repeat(2, 1fr);
  }
  .km-grid--3-col {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 992px) {
    .km-grid--3-col {
      grid-template-columns: repeat(3, 1fr);
    }
}

/* ---------------------------------------- */
/*             COMPONENTS                 */
/* ---------------------------------------- */

/* Buttons */
.km-button {
  display: inline-block;
  padding: 0.75rem 1.75rem;
  border-radius: var(--km-border-radius-buttons);
  font-weight: var(--km-fw-bold);
  text-align: center;
  cursor: pointer;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  border: 1px solid transparent;
}

.km-button--primary {
  background-color: var(--km-color-primary);
  color: var(--km-color-white);
}

.km-button--primary:hover {
  transform: scale(1.05);
  box-shadow: var(--km-shadow-medium);
  color: var(--km-color-white);
}

.km-button--secondary {
  background-color: transparent;
  color: var(--km-color-neutral-dark);
  border-color: var(--km-color-accent);
}

.km-button--secondary:hover {
  background-color: var(--km-color-neutral-light);
  border-color: var(--km-color-primary);
  color: var(--km-color-primary);
}

/* Media (Image) Container */
.km-media {
    overflow: hidden;
    border-radius: var(--km-border-radius-cards);
}
.km-media img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
}
.km-media--aspect-16-9 { aspect-ratio: 16 / 9; }
.km-media--aspect-4-3 { aspect-ratio: 4 / 3; }
.km-media--aspect-3-2 { aspect-ratio: 3 / 2; }
.km-media--aspect-1-1 { aspect-ratio: 1 / 1; }


/* Header & Navigation */
.km-header {
  position: sticky;
  top: 0;
  z-index: 100;
  background-color: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--km-color-border);
  padding: 1rem 0;
}

.km-header__container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.km-header__logo img {
    height: 32px;
    width: auto;
}

.km-nav__toggle {
  display: none;
  background: none;
  border: none;
  cursor: pointer;
  z-index: 101;
}

.km-nav__toggle .icon-close { display: none; }

.km-nav {
  display: flex;
  align-items: center;
  gap: 1.5rem;
}

.km-nav__list {
  display: flex;
  gap: 1.5rem;
}

.km-nav__link {
  color: var(--km-color-text);
  font-weight: var(--km-fw-bold);
  position: relative;
  padding: 0.5rem 0;
}

.km-nav__link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background-color: var(--km-color-primary);
    transform: scaleX(0);
    transform-origin: right;
    transition: transform 0.3s ease;
}

.km-nav__link:hover::after, .km-nav__link.is-active::after {
    transform: scaleX(1);
    transform-origin: left;
}
.km-nav__link.is-active { color: var(--km-color-primary); }

/* Hero Section */
.km-hero {
    background-color: var(--km-color-neutral-light);
    padding: var(--km-spacing-section-y) 0;
}

.km-hero__container {
    display: grid;
    align-items: center;
    gap: 3rem;
    grid-template-columns: 1fr;
}

@media(min-width: 992px) {
    .km-hero__container {
        grid-template-columns: 1fr 1.2fr;
    }
    .km-hero__image-wrapper {
        order: -1;
    }
}

.km-hero__badge {
    display: inline-block;
    margin-bottom: 1rem;
    padding: 0.25rem 0.75rem;
    background-color: var(--km-color-accent-bold);
    color: var(--km-color-neutral-dark);
    border-radius: var(--km-border-radius-buttons);
    font-size: var(--km-fs-small);
    font-weight: var(--km-fw-bold);
}

.km-hero__headline {
    margin-bottom: 1rem;
}

.km-hero__subline {
    margin-bottom: 2rem;
    max-width: 50ch;
}

.km-hero__actions {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
}

/* Logos Section */
.km-logos {
    padding: 2rem 0;
    text-align: center;
}
.km-logos__headline {
    font-size: 1rem;
    color: var(--km-color-accent);
    font-weight: var(--km-fw-bold);
    margin-bottom: 2rem;
}
.km-logos__marquee {
    overflow: hidden;
    position: relative;
    width: 100%;
    -webkit-mask-image: linear-gradient(to right, transparent, white 20%, white 80%, transparent);
    mask-image: linear-gradient(to right, transparent, white 20%, white 80%, transparent);
}
.km-logos__track {
    display: flex;
    width: max-content;
    animation: marquee 30s linear infinite;
}
.km-logos__track span {
    font-size: 1.2rem;
    color: var(--km-color-accent);
    font-weight: var(--km-fw-bold);
    margin: 0 2.5rem;
}
@keyframes marquee {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
}

/* Feature Cards */
.km-feature-card {
    text-align: center;
    padding: 2rem;
    background-color: var(--km-color-white);
    border-radius: var(--km-border-radius-cards);
    border: 1px solid var(--km-color-border);
}
.km-feature-card__icon {
    display: inline-flex;
    padding: 1rem;
    background-color: var(--km-color-neutral-light);
    border-radius: 50%;
    margin-bottom: 1.5rem;
}
.km-feature-card__icon svg {
    width: 24px;
    height: 24px;
    color: var(--km-color-primary);
}
.km-feature-card h3 {
    margin-bottom: 0.5rem;
}

/* Service Cards */
.km-service-card {
    background-color: var(--km-color-white);
    border: 1px solid var(--km-color-border);
    border-radius: var(--km-border-radius-cards);
    overflow: hidden;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}
.km-service-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--km-shadow-medium);
}
.km-service-card__content {
    padding: 1.5rem;
}
.km-service-card h3 {
    margin-bottom: 0.75rem;
}
.km-service-card p {
    margin-bottom: 1rem;
}
.km-service-card ul {
    margin-bottom: 1.5rem;
    padding-left: 1.25rem;
}
.km-service-card ul li {
    list-style-type: '✓';
    padding-left: 0.75rem;
    margin-bottom: 0.5rem;
}

/* Process Stepper */
.km-process-stepper {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 2rem;
}
.km-process-stepper::before {
    content: '';
    position: absolute;
    left: 24px;
    top: 24px;
    bottom: 24px;
    width: 2px;
    background-color: var(--km-color-border);
}
.km-process-step {
    display: flex;
    align-items: flex-start;
    gap: 1.5rem;
    position: relative;
}
.km-process-step__number {
    width: 50px;
    height: 50px;
    flex-shrink: 0;
    display: grid;
    place-items: center;
    border-radius: 50%;
    background-color: var(--km-color-primary);
    color: var(--km-color-white);
    font-weight: var(--km-fw-bold);
    font-size: 1.25rem;
    z-index: 1;
    border: 4px solid var(--km-color-white);
}
.km-process-step h4 {
    margin-bottom: 0.5rem;
}
@media (min-width: 768px) {
    .km-process-stepper {
        flex-direction: row;
        gap: var(--km-spacing-grid-gap);
    }
    .km-process-stepper::before {
        left: 24px;
        right: 24px;
        top: 24px;
        bottom: auto;
        width: auto;
        height: 2px;
    }
    .km-process-step {
        flex-direction: column;
        align-items: center;
        text-align: center;
        flex: 1;
    }
    .km-process-step__number {
        margin-bottom: 1rem;
    }
}

/* Portfolio Cards */
.km-portfolio-card {
    border-radius: var(--km-border-radius-cards);
    overflow: hidden;
    border: 1px solid var(--km-color-border);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}
.km-portfolio-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--km-shadow-medium);
}
.km-portfolio-card__content {
    padding: 1rem;
}
.km-portfolio-card__content h4 {
    margin-bottom: 0.25rem;
}
.km-portfolio-card__content span {
    color: var(--km-color-accent);
    font-size: var(--km-fs-small);
}

/* CTA Band */
.km-cta-band {
    background-color: var(--km-color-neutral-dark);
    color: var(--km-color-white);
    padding: var(--km-spacing-section-y) 0;
    text-align: center;
}
.km-cta-band h2 {
    color: var(--km-color-white);
    margin-bottom: 1rem;
}
.km-cta-band p {
    color: var(--km-color-accent);
    margin-bottom: 2rem;
}

/* FAQ Accordion */
.km-faq-item {
    border-bottom: 1px solid var(--km-color-border);
}
.km-faq-item:first-child {
    border-top: 1px solid var(--km-color-border);
}
.km-faq-question {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 0;
    background: none;
    border: none;
    cursor: pointer;
    text-align: left;
    font-size: var(--km-fs-h4);
    font-family: var(--km-font-family-headings);
    font-weight: var(--km-fw-bold);
    color: var(--km-color-neutral-dark);
}
.km-faq-icon {
    transition: transform 0.3s ease;
    flex-shrink: 0;
    margin-left: 1rem;
}
.km-faq-question[aria-expanded="true"] .km-faq-icon {
    transform: rotate(180deg);
}
.km-faq-answer {
    overflow: hidden;
    transition: max-height 0.3s ease-out;
    max-height: 0;
}
.km-faq-answer p {
    padding-bottom: 1.5rem;
    max-width: 65ch;
}

/* Contact Form */
.km-contact-form {
    display: grid;
    gap: 1.5rem;
}
.km-form-group {
    display: flex;
    flex-direction: column;
}
.km-form-group label {
    margin-bottom: 0.5rem;
    font-weight: var(--km-fw-bold);
}
.km-form-group input, .km-form-group textarea {
    padding: 0.75rem 1rem;
    border: 1px solid var(--km-color-border);
    border-radius: 8px;
    font-family: inherit;
    font-size: 1rem;
    transition: border-color 0.2s, box-shadow 0.2s;
}
.km-form-group input:focus, .km-form-group textarea:focus {
    outline: none;
    border-color: var(--km-color-primary);
    box-shadow: 0 0 0 3px rgba(15, 118, 110, 0.2);
}

/* Footer */
.km-footer {
    background-color: var(--km-color-neutral-light);
    padding: 2rem 0;
    border-top: 1px solid var(--km-color-border);
    font-size: var(--km-fs-small);
    color: var(--km-color-accent);
}
.km-footer__content {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
}
.km-footer__nav {
    display: flex;
    gap: 1.5rem;
}
.km-footer__nav a { color: var(--km-color-accent); }
.km-footer__nav a:hover { color: var(--km-color-primary); }
@media (min-width: 768px) {
    .km-footer__content {
        flex-direction: row;
        justify-content: space-between;
    }
}

/* ---------------------------------------- */
/*           ANIMATIONS & MOBILE          */
/* ---------------------------------------- */

/* Scroll Reveal */
.reveal {
  opacity: 0;
  transform: translateY(20px);
  transition: opacity 0.5s ease-out, transform 0.5s ease-out;
  transition-delay: var(--animation-delay, 0s);
}

.reveal.is-visible {
  opacity: 1;
  transform: translateY(0);
}

@media (prefers-reduced-motion: reduce) {
  .reveal {
    transition: none;
  }
}

/* Mobile Navigation */
@media (max-width: 991px) {
    .km-nav__toggle {
        display: block;
    }
    .km-nav {
        position: fixed;
        top: 0;
        right: 0;
        bottom: 0;
        width: 100%;
        max-width: 300px;
        background-color: var(--km-color-white);
        flex-direction: column;
        align-items: flex-start;
        padding: 6rem 2rem 2rem;
        transform: translateX(100%);
        transition: transform 0.3s ease-in-out;
        box-shadow: -5px 0 15px rgba(0,0,0,0.1);
    }
    body.nav-open .km-nav {
        transform: translateX(0);
    }
    body.nav-open .km-nav__toggle .icon-menu { display: none; }
    body.nav-open .km-nav__toggle .icon-close { display: block; }

    .km-nav__list {
        flex-direction: column;
        width: 100%;
        gap: 0.5rem;
    }
    .km-nav__link {
        display: block;
        padding: 1rem 0;
        font-size: 1.2rem;
    }
    .km-nav__cta {
        margin-top: 1.5rem;
        width: 100%;
    }
}

document.addEventListener('DOMContentLoaded', () => {

  // Mobile Navigation Toggle
  const navToggle = document.querySelector('.km-nav__toggle');
  const mainNav = document.querySelector('.km-nav');
  if (navToggle && mainNav) {
    navToggle.addEventListener('click', () => {
      const isExpanded = navToggle.getAttribute('aria-expanded') === 'true';
      navToggle.setAttribute('aria-expanded', !isExpanded);
      document.body.classList.toggle('nav-open');
    });

    mainNav.querySelectorAll('.km-nav__link').forEach(link => {
        link.addEventListener('click', () => {
            document.body.classList.remove('nav-open');
            navToggle.setAttribute('aria-expanded', 'false');
        });
    });
  }

  // FAQ Accordion
  const faqItems = document.querySelectorAll('.km-faq-item');
  faqItems.forEach(item => {
    const question = item.querySelector('.km-faq-question');
    const answer = item.querySelector('.km-faq-answer');

    if (question && answer) {
      question.addEventListener('click', () => {
        const isExpanded = question.getAttribute('aria-expanded') === 'true';
        question.setAttribute('aria-expanded', !isExpanded);
        answer.hidden = isExpanded;
        if (!isExpanded) {
          answer.style.maxHeight = answer.scrollHeight + 'px';
        } else {
          answer.style.maxHeight = '0';
        }
      });
    }
  });

  // Scroll Reveal Animation
  const revealObserver = new IntersectionObserver((entries, observer) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('is-visible');
        observer.unobserve(entry.target);
      }
    });
  }, { threshold: 0.1 });

  document.querySelectorAll('.reveal').forEach(el => {
    revealObserver.observe(el);
  });

  // Scroll Spy for Active Nav Links
  const sectionObserver = new IntersectionObserver((entries) => {
    entries.forEach(entry => {
        const id = entry.target.getAttribute('id');
        const navLink = document.querySelector(`.km-nav__link[href="#${id}"]`);
        if (navLink) {
            if (entry.isIntersecting && entry.intersectionRatio > 0.5) {
                document.querySelectorAll('.km-nav__link').forEach(link => link.classList.remove('is-active'));
                navLink.classList.add('is-active');
            }
        }
    });
  }, { rootMargin: '-80px 0px -50% 0px', threshold: 0.5 });

  document.querySelectorAll('main section[id]').forEach(section => {
    sectionObserver.observe(section);
  });

  // Smooth Scroll for anchor links
  document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function (e) {
        e.preventDefault();
        const targetId = this.getAttribute('href');
        const targetElement = document.querySelector(targetId);
        if(targetElement) {
            const headerOffset = 80;
            const elementPosition = targetElement.getBoundingClientRect().top;
            const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

            window.scrollTo({
                 top: offsetPosition,
                 behavior: "smooth"
            });
        }
    });
  });

  // Contact Form Submission
  const contactForm = document.getElementById('contact-form');
  if (contactForm) {
    contactForm.addEventListener('submit', async (e) => {
      e.preventDefault();
      const submitButton = contactForm.querySelector('button[type="submit"]');
      const formData = new FormData(contactForm);
      const data = Object.fromEntries(formData.entries());

      submitButton.disabled = true;
      submitButton.textContent = 'Sende...';

      try {
        const response = await fetch('/api/contact', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
        });

        if (response.ok) {
          submitButton.textContent = 'Erfolgreich gesendet!';
          contactForm.reset();
        } else {
          throw new Error('Network response was not ok.');
        }
      } catch (error) {
        console.error('Form submission error:', error);
        submitButton.textContent = 'Fehler! Erneut versuchen.';
      } finally {
        setTimeout(() => {
            submitButton.disabled = false;
            submitButton.textContent = 'Nachricht senden';
        }, 3000);
      }
    });
  }

});
