/* CSS */
:root {
    --color-primary: #0a2d4d; /* Deep sea blue */
    --color-accent: #e87a58;  /* Warm sand/coral */
    --color-background: #f8f9fa; /* Light grey */
    --color-surface: #ffffff;
    --color-text: #343a40;
    --color-text-light: #f8f9fa;
    --color-muted: #6c757d;
    --color-border: #dee2e6;

    --font-display: 'Playfair Display', serif;
    --font-body: 'Inter', sans-serif;

    --radius-sm: 4px;
    --radius-md: 8px;
    --radius-lg: 16px;

    --shadow-sm: 0 1px 3px rgba(0,0,0,0.05);
    --shadow-md: 0 4px 10px rgba(0,0,0,0.07);

    --timing-fast: 0.2s;
    --easing-standard: cubic-bezier(0.25, 0.1, 0.25, 1);
}

/* Reset & Base */
*,
*::before,
*::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    scroll-behavior: smooth;
    font-size: 100%;
}

body {
    font-family: var(--font-body);
    background-color: var(--color-background);
    color: var(--color-text);
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
}

/* Typography */
.km-h1, .km-h2, .km-h3, .km-h4 {
    font-family: var(--font-display);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-primary);
}

.km-h1 { font-size: clamp(2.5rem, 5vw, 4rem); letter-spacing: -0.02em; }
.km-h2 { font-size: clamp(2rem, 4vw, 3rem); }
.km-h3 { font-size: clamp(1.5rem, 3vw, 2.25rem); }
.km-h4 { font-size: clamp(1.2rem, 2.5vw, 1.5rem); }

p, li, a, span, label, input, textarea, button {
    font-size: clamp(1rem, 1.6vw, 1.125rem);
    max-width: 65ch;
}

.km-kicker {
    display: block;
    font-size: 0.9rem;
    font-weight: 600;
    color: var(--color-accent);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
}

.km-subheadline {
    font-size: clamp(1.1rem, 2vw, 1.25rem);
    color: var(--color-muted);
    margin-top: 1rem;
}

/* Layout */
.km-container {
    max-width: 1200px;
    margin-left: auto;
    margin-right: auto;
    padding-left: 1.5rem;
    padding-right: 1.5rem;
}

.km-container--md {
    max-width: 800px;
}

.km-section {
    padding: clamp(4rem, 8vw, 6rem) 0;
}

.km-section--surface { background-color: var(--color-surface); }
.km-section--dark {
    background-color: var(--color-primary);
    color: var(--color-text-light);
}
.km-section--dark .km-h2, .km-section--dark .km-h3, .km-section--dark .km-h4 { color: var(--color-text-light); }
.km-section--dark p { color: rgba(255,255,255,0.8); }

.km-section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.km-section-header > *:not(.km-kicker) { margin-left: auto; margin-right: auto;}

.km-text-center { text-align: center; }
.km-text-center > * { margin-left: auto; margin-right: auto; }

/* Grid */
.km-grid {
    display: grid;
    gap: 2rem;
}

@media (min-width: 768px) {
    .km-grid--2col { grid-template-columns: repeat(2, 1fr); }
    .km-grid--3col { grid-template-columns: repeat(3, 1fr); }
}

/* Buttons */
.km-btn {
    display: inline-block;
    padding: 0.8rem 1.8rem;
    border-radius: 50px;
    text-decoration: none;
    font-weight: 600;
    font-size: 1rem;
    text-align: center;
    border: 2px solid transparent;
    transition: transform var(--timing-fast) var(--easing-standard), background-color var(--timing-fast) var(--easing-standard);
}

.km-btn--primary {
    background-color: var(--color-accent);
    color: var(--color-surface);
}

.km-btn--primary:hover, .km-btn--primary:focus-visible {
    background-color: #d16a48;
    transform: translateY(-2px);
}

.km-btn--secondary {
    background-color: transparent;
    color: var(--color-primary);
    border-color: var(--color-border);
}

.km-btn--secondary:hover, .km-btn--secondary:focus-visible {
    background-color: var(--color-surface);
    border-color: var(--color-primary);
    transform: translateY(-2px);
}

/* Header */
.km-header {
    position: sticky;
    top: 0;
    width: 100%;
    z-index: 100;
    padding: 1.5rem 0;
    background-color: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--color-border);
    transition: padding var(--timing-fast) var(--easing-standard), box-shadow var(--timing-fast) var(--easing-standard);
}

.js .km-header--scrolled {
    padding: 1rem 0;
    box-shadow: var(--shadow-md);
}

.km-header__inner {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.km-header__logo {
    font-family: var(--font-display);
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-primary);
    text-decoration: none;
}

.km-header__nav {
    display: none;
}

@media (min-width: 768px) {
    .km-header__nav { display: block; }
    .km-header__nav-list {
        display: flex;
        list-style: none;
        gap: 2rem;
    }
    .km-header__nav-link {
        text-decoration: none;
        color: var(--color-text);
        font-weight: 600;
        position: relative;
    }
    .km-header__nav-link::after {
        content: '';
        position: absolute;
        bottom: -5px;
        left: 0;
        width: 100%;
        height: 2px;
        background-color: var(--color-accent);
        transform: scaleX(0);
        transform-origin: right;
        transition: transform var(--timing-fast) var(--easing-standard);
    }
    .km-header__nav-link:hover::after, .km-header__nav-link.is-active::after {
        transform: scaleX(1);
        transform-origin: left;
    }
}

.km-header__cta {
    display: none;
}
@media (min-width: 768px) {
    .km-header__cta { display: inline-block; }
}

.km-header__mobile-toggle {
    display: block;
    width: 44px;
    height: 44px;
    background: none;
    border: none;
    cursor: pointer;
    z-index: 101;
}
@media (min-width: 768px) {
    .km-header__mobile-toggle { display: none; }
}

.km-header__toggle-icon,
.km-header__toggle-icon::before,
.km-header__toggle-icon::after {
    content: '';
    display: block;
    width: 24px;
    height: 2px;
    background-color: var(--color-primary);
    margin: 0 auto;
    position: relative;
    transition: transform 0.3s, top 0.3s;
}

.km-header__toggle-icon::before { top: -8px; }
.km-header__toggle-icon::after { top: 8px; }

.km-mobile-menu-open .km-header__toggle-icon { background: transparent; }
.km-mobile-menu-open .km-header__toggle-icon::before { transform: rotate(45deg); top: 0; }
.km-mobile-menu-open .km-header__toggle-icon::after { transform: rotate(-45deg); top: 0; }

/* Mobile Menu */
.km-mobile-menu {
    position: fixed;
    top: 0; left: 0; right: 0; bottom: 0;
    background-color: var(--color-surface);
    z-index: 99;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transform: translateX(100%);
    transition: transform 0.4s var(--easing-standard);
    visibility: hidden;
}
.km-mobile-menu-open .km-mobile-menu { transform: translateX(0); visibility: visible; }

.km-mobile-menu__nav ul { list-style: none; text-align: center; }
.km-mobile-menu__nav li { margin-bottom: 2rem; }
.km-mobile-menu__nav a {
    font-family: var(--font-display);
    font-size: 2.5rem;
    color: var(--color-primary);
    text-decoration: none;
}
.km-mobile-menu .km-btn { margin-top: 2rem; }

/* Hero Section */
.km-hero {
    background-image: linear-gradient(to top, #f3e9e3 0%, #ffffff 100%);
    padding: clamp(5rem, 10vw, 8rem) 0;
    text-align: center;
}
.km-hero__content > * { margin-left: auto; margin-right: auto; }
.km-hero__actions {
    margin-top: 2rem;
    display: flex;
    justify-content: center;
    gap: 1rem;
    flex-wrap: wrap;
}

/* Components */
.km-card {
    background-color: var(--color-surface);
    padding: 2rem;
    border-radius: var(--radius-md);
    box-shadow: var(--shadow-sm);
    border: 1px solid var(--color-border);
    height: 100%;
}

.km-card--dark {
    background-color: rgba(255,255,255,0.05);
    border-color: rgba(255,255,255,0.2);
}

.km-feature-list {
    list-style: none;
    display: grid;
    gap: 1.5rem;
}
@media (min-width: 768px) { .km-feature-list { grid-template-columns: 1fr 1fr;} }

.km-feature-list li {
    padding-left: 2rem;
    position: relative;
}
.km-feature-list li::before {
    content: '✓';
    position: absolute;
    left: 0;
    color: var(--color-accent);
    font-weight: bold;
}

/* Contact Form */
.km-form-grid {
    display: grid;
    gap: 1.5rem;
}
.km-form-group { display: flex; flex-direction: column; }
.km-form-group label { margin-bottom: 0.5rem; font-weight: 600; }
.km-form-group input, .km-form-group textarea {
    width: 100%;
    padding: 0.8rem 1rem;
    border-radius: var(--radius-sm);
    border: 1px solid var(--color-border);
    font-family: var(--font-body);
    font-size: 1rem;
}
.km-form-group input:focus-visible, .km-form-group textarea:focus-visible {
    outline: 2px solid var(--color-accent);
    outline-offset: 2px;
    border-color: var(--color-accent);
}
.km-form-group textarea { min-height: 150px; resize: vertical; }

/* Footer */
.km-footer {
    background-color: var(--color-primary);
    color: var(--color-text-light);
    padding: 4rem 0 2rem 0;
    margin-top: auto;
}
.km-footer__inner {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 2rem;
}
@media (min-width: 768px) {
    .km-footer__inner { flex-direction: row; justify-content: space-between; text-align: left; }
}
.km-footer__logo {
    font-family: var(--font-display);
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--color-text-light);
    text-decoration: none;
}
.km-footer p { color: rgba(255,255,255,0.7); max-width: none; }
.km-footer__nav ul { list-style: none; display: flex; gap: 1.5rem; }
.km-footer__nav a { color: var(--color-text-light); text-decoration: none; }
.km-footer__legal { font-size: 0.9rem; }

/* Accessibility & Animations */
.km-skip-link {
    position: absolute;
    top: -100px;
    left: 1rem;
    background: var(--color-primary);
    color: white;
    padding: 1rem;
    z-index: 9999;
    transition: top 0.3s;
}
.km-skip-link:focus {
    top: 1rem;
}

.reveal { opacity: 1; transform: none; }
.js .reveal {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s var(--easing-standard), transform 0.6s var(--easing-standard);
}
.js .reveal.is-visible {
    opacity: 1;
    transform: translateY(0);
}
.js .reveal:nth-child(2) { transition-delay: 100ms; }
.js .reveal:nth-child(3) { transition-delay: 200ms; }
.js .reveal:nth-child(4) { transition-delay: 300ms; }

// JS
document.documentElement.classList.add('js');

document.addEventListener('DOMContentLoaded', () => {

    const initStickyHeader = () => {
        const header = document.querySelector('.km-header');
        if (!header) return;

        const observer = new IntersectionObserver(([entry]) => {
            header.classList.toggle('km-header--scrolled', entry.boundingClientRect.top < -100);
        }, { threshold: [1] });

        // Create a dummy element to observe
        const sentinel = document.createElement('div');
        sentinel.style.position = 'absolute';
        sentinel.style.top = '100px';
        sentinel.style.height = '1px';
        document.body.prepend(sentinel);
        observer.observe(sentinel);
    };

    const initMobileMenu = () => {
        const toggleBtn = document.querySelector('.km-header__mobile-toggle');
        const mobileMenu = document.getElementById('mobile-menu');
        if (!toggleBtn || !mobileMenu) return;

        toggleBtn.addEventListener('click', () => {
            const isExpanded = toggleBtn.getAttribute('aria-expanded') === 'true';
            toggleBtn.setAttribute('aria-expanded', !isExpanded);
            document.body.classList.toggle('km-mobile-menu-open');
        });

        // Close menu when a link is clicked
        mobileMenu.querySelectorAll('a').forEach(link => {
            link.addEventListener('click', () => {
                toggleBtn.setAttribute('aria-expanded', 'false');
                document.body.classList.remove('km-mobile-menu-open');
            });
        });
    };

    const initScrollReveal = () => {
        const revealElements = document.querySelectorAll('.reveal');
        if (revealElements.length === 0) return;

        const observer = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('is-visible');
                    observer.unobserve(entry.target);
                }
            });
        }, { rootMargin: '0px 0px -10% 0px' });

        revealElements.forEach(el => observer.observe(el));
    };

    initStickyHeader();
    initMobileMenu();
    initScrollReveal();
});
