:root {
    --color-primary: #0d9488;
    --color-secondary: #083344;
    --color-accent: #f59e0b;
    --color-neutral: #64748b;
    --color-background: #ffffff;
    --color-background-light: #f8fafc;
    --color-text: #0f172a;
    --color-text-light: #ffffff;

    --font-family-display: 'Helvetica Neue', 'Arial', sans-serif;
    --font-family-body: 'Helvetica Neue', 'Arial', sans-serif;

    --font-weight-regular: 400;
    --font-weight-bold: 700;

    --border-radius-sm: 4px;
    --border-radius-md: 8px;
    --border-radius-lg: 16px;

    --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1);

    --transition-duration: 0.3s;
    --transition-timing: ease-in-out;

    --container-width: 1240px;
    --container-padding: 1.5rem;

    --section-gap: clamp(4.5rem, 10vw, 7.5rem);
}

/* --- RESET & BASE --- */
*,
*::before,
*::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    scroll-behavior: smooth;
    scroll-padding-top: 80px; /* Adjust for sticky header */
}

body {
    font-family: var(--font-family-body);
    font-weight: var(--font-weight-regular);
    line-height: 1.65;
    color: var(--color-text);
    background-color: var(--color-background);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

img, picture, video, canvas, svg {
    display: block;
    max-width: 100%;
}

button, input, select, textarea {
    font: inherit;
}

h1, h2, h3, h4 {
    font-family: var(--font-family-display);
    font-weight: var(--font-weight-bold);
    line-height: 1.2;
    color: var(--color-secondary);
}

h1 { font-size: clamp(2.5rem, 5vw, 4rem); }
h2 { font-size: clamp(2rem, 4vw, 3rem); }
h3 { font-size: 1.5rem; }
h4 { font-size: 1.25rem; }

p {
    margin-bottom: 1rem;
    max-width: 65ch;
}

a {
    color: var(--color-primary);
    text-decoration: none;
    transition: color var(--transition-duration) var(--transition-timing);
}

a:hover {
    color: var(--color-accent);
}

ul {
    list-style: none;
}

/* --- UTILITIES --- */
.container {
    width: 100%;
    max-width: var(--container-width);
    margin-left: auto;
    margin-right: auto;
    padding-left: var(--container-padding);
    padding-right: var(--container-padding);
}

.section-padding { padding-top: var(--section-gap); padding-bottom: var(--section-gap); }
.section-padding--sm { padding-top: calc(var(--section-gap) / 2); padding-bottom: calc(var(--section-gap) / 2); }
.bg-light { background-color: var(--color-background-light); }
.text-center { text-align: center; }
.text-center p { margin-left: auto; margin-right: auto; }

/* --- BUTTONS --- */
.btn {
    display: inline-block;
    padding: 0.75rem 1.75rem;
    min-height: 48px;
    border-radius: var(--border-radius-md);
    font-weight: var(--font-weight-bold);
    text-align: center;
    cursor: pointer;
    border: 2px solid transparent;
    transition: transform var(--transition-duration) var(--transition-timing), box-shadow var(--transition-duration) var(--transition-timing), background-color var(--transition-duration) var(--transition-timing);
}

.btn--primary {
    background-color: var(--color-primary);
    color: var(--color-text-light);
}

.btn--primary:hover {
    background-color: var(--color-accent);
    color: var(--color-secondary);
    transform: translateY(-3px);
    box-shadow: var(--shadow-lg);
}

.btn--secondary {
    background-color: transparent;
    color: var(--color-secondary);
    border-color: var(--color-secondary);
}

.btn--secondary:hover {
    background-color: var(--color-secondary);
    color: var(--color-text-light);
    transform: translateY(-3px);
    box-shadow: var(--shadow-lg);
}

.btn:focus-visible {
    outline: 3px solid var(--color-accent);
    outline-offset: 2px;
}

/* --- HEADER --- */
.site-header {
    padding: 1.5rem 0;
    background-color: rgba(255, 255, 255, 0.8);
    transition: padding var(--transition-duration) var(--transition-timing), box-shadow var(--transition-duration) var(--transition-timing);
}

.sticky-header {
    position: sticky;
    top: 0;
    width: 100%;
    z-index: 1000;
}

.site-header.is-scrolled {
    padding: 0.75rem 0;
    box-shadow: var(--shadow-md);
    backdrop-filter: blur(8px);
}

.site-header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-weight: var(--font-weight-bold);
    font-size: 1.25rem;
    color: var(--color-secondary);
}

.main-nav ul {
    display: flex;
    gap: 2rem;
}

.main-nav a {
    color: var(--color-text);
    font-weight: var(--font-weight-bold);
    position: relative;
    padding: 0.5rem 0;
}

.main-nav a::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background-color: var(--color-primary);
    transform: scaleX(0);
    transform-origin: right;
    transition: transform var(--transition-duration) var(--transition-timing);
}

.main-nav a:hover::after, .main-nav a.active::after {
    transform: scaleX(1);
    transform-origin: left;
}

.mobile-nav-toggle {
    display: none;
    background: none;
    border: none;
    cursor: pointer;
    z-index: 1001;
}

/* --- HERO --- */
.hero {
    position: relative;
    min-height: 80vh;
    display: flex;
    align-items: center;
    color: var(--color-text-light);
    padding: 6rem 0;
}

.hero-background {
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-size: cover;
    background-position: center;
    z-index: -2;
}

.hero-overlay {
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background: linear-gradient(90deg, rgba(8, 51, 68, 0.9) 0%, rgba(8, 51, 68, 0.6) 100%);
    z-index: -1;
}

.hero-content {
    max-width: 60%;
}

.hero-eyebrow { display: block; margin-bottom: 1rem; font-weight: var(--font-weight-bold); color: var(--color-accent); }
.hero-headline { color: var(--color-text-light); margin-bottom: 1.5rem; }
.hero-subheadline { font-size: 1.2rem; max-width: 55ch; margin-bottom: 2.5rem; }
.hero-cta-group { display: flex; gap: 1rem; flex-wrap: wrap; }

/* --- SIGNATURE DIVIDER --- */
.signature-divider {
    line-height: 0;
    margin-top: -1px;
}
.signature-divider-inverse {
    line-height: 0;
    margin-bottom: -1px;
}

/* --- SECTIONS --- */
.section-header { margin-bottom: 3rem; }
.section-title { margin-bottom: 1rem; }
.section-subtitle { font-size: 1.15rem; color: var(--color-neutral); max-width: 60ch; }

/* Features Section */
.features-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.feature-card { background: var(--color-background); padding: 2rem; border-radius: var(--border-radius-lg); box-shadow: var(--shadow-md); transition: transform var(--transition-duration) var(--transition-timing), box-shadow var(--transition-duration) var(--transition-timing); }
.feature-card:hover { transform: translateY(-5px); box-shadow: var(--shadow-lg); }
.feature-icon { font-size: 2rem; color: var(--color-primary); margin-bottom: 1rem; }
.feature-title { margin-bottom: 0.5rem; }

/* About Teaser Section */
.about-teaser-grid { display: grid; grid-template-columns: 1fr 1.5fr; gap: 3rem; align-items: center; }
.about-teaser-image-wrapper { border-radius: var(--border-radius-lg); overflow: hidden; box-shadow: var(--shadow-lg); }
.about-teaser-image { width: 100%; height: 100%; object-fit: cover; }

/* Services Overview */
.services-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; margin-bottom: 3rem; }
.service-card { border: 1px solid #e2e8f0; padding: 2rem; border-radius: var(--border-radius-lg); transition: transform var(--transition-duration) var(--transition-timing), box-shadow var(--transition-duration) var(--transition-timing); }
.service-card:hover { transform: translateY(-5px); box-shadow: var(--shadow-md); border-color: var(--color-primary); }
.service-title { margin-bottom: 1rem; }
.service-link { font-weight: var(--font-weight-bold); }

/* CTA Section */
.cta-section .section-title { color: var(--color-secondary); }

/* --- FOOTER --- */
.site-footer { background-color: var(--color-secondary); color: #cbd5e1; padding: 4rem 0 2rem; }
.footer-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(220px, 1fr)); gap: 2rem; margin-bottom: 3rem; }
.footer-heading { color: var(--color-text-light); margin-bottom: 1rem; font-size: 1.1rem; }
.footer-nav a { color: #cbd5e1; display: block; padding: 0.25rem 0; }
.footer-nav a:hover { color: var(--color-accent); }
.footer-bottom { border-top: 1px solid #334155; padding-top: 2rem; text-align: center; font-size: 0.9rem; }

/* --- PAGE-SPECIFIC --- */
/* Leistungen Page */
.page-header-section { padding-top: var(--section-gap); }
.page-title { margin-bottom: 1rem; }
.page-subtitle { max-width: 70ch; margin: 0 auto; color: var(--color-neutral); }
.service-item { display: grid; grid-template-columns: 1fr 1fr; gap: 3rem; align-items: center; margin-bottom: var(--section-gap); }
.service-item.reverse { grid-template-columns: 1fr 1fr; }
.service-item.reverse .service-item-image { order: 2; }
.service-item-image img { border-radius: var(--border-radius-lg); box-shadow: var(--shadow-lg); width: 100%; height: auto; object-fit: cover; }
.service-item-title { margin-bottom: 1.5rem; }
.image-placeholder { width: 100%; aspect-ratio: 4/3; background: linear-gradient(135deg, var(--color-background-light), #e2e8f0); border-radius: var(--border-radius-lg); }

/* √úber Uns Page */
.about-main-grid { display: grid; grid-template-columns: 1fr 1.5fr; gap: 3rem; align-items: flex-start; }
.about-main-image img { border-radius: var(--border-radius-lg); box-shadow: var(--shadow-lg); }

/* FAQ Page */
.faq-accordion { max-width: 800px; margin: 0 auto; }
.faq-item { border-bottom: 1px solid #e2e8f0; }
.faq-question { width: 100%; background: none; border: none; text-align: left; padding: 1.5rem 0; font-size: 1.2rem; font-weight: var(--font-weight-bold); cursor: pointer; display: flex; justify-content: space-between; align-items: center; }
.faq-answer { max-height: 0; overflow: hidden; transition: max-height 0.4s ease-in-out; }
.faq-answer p { padding: 0 0 1.5rem; }
.faq-icon { width: 1em; height: 1em; transition: transform 0.4s ease-in-out; }
.faq-icon::before, .faq-icon::after { content: ''; position: absolute; background-color: var(--color-primary); width: 1em; height: 2px; }
.faq-icon::after { transform: rotate(90deg); }
.faq-question[aria-expanded='true'] .faq-icon { transform: rotate(45deg); }

/* Kontakt Page */
.contact-grid { display: grid; grid-template-columns: 1.5fr 1fr; gap: 3rem; }
.contact-form .form-group { margin-bottom: 1.5rem; }
.contact-form label { display: block; margin-bottom: 0.5rem; font-weight: var(--font-weight-bold); }
.contact-form input, .contact-form textarea { width: 100%; padding: 0.75rem; border: 1px solid #cbd5e1; border-radius: var(--border-radius-md); }
.contact-form input:focus, .contact-form textarea:focus { outline: 2px solid var(--color-primary); border-color: transparent; }
.form-group-checkbox { display: flex; align-items: flex-start; gap: 0.5rem; margin-bottom: 1.5rem; }
.form-group-checkbox input { margin-top: 0.25em; }
.contact-details h4 { margin-top: 2rem; margin-bottom: 0.5rem; }
.contact-details h4:first-child { margin-top: 0; }
.contact-map-image { margin-top: 2rem; border-radius: var(--border-radius-lg); overflow: hidden; }

/* Legal Pages */
.legal-page-section .content-narrow { max-width: 800px; }
.legal-page-section h2 { margin-top: 2rem; margin-bottom: 1rem; }

/* --- PREMIUM FEATURES --- */
/* Carousel */
.carousel-wrapper { position: relative; }
.carousel { display: flex; overflow-x: auto; scroll-snap-type: x mandatory; scrollbar-width: none; -ms-overflow-style: none; }
.carousel::-webkit-scrollbar { display: none; }
.carousel-slide { flex: 0 0 100%; scroll-snap-align: start; padding: 0 0.5rem; }
.carousel-slide img { border-radius: var(--border-radius-lg); width: 100%; height: auto; object-fit: cover; aspect-ratio: 16/9; }
.carousel-btn { position: absolute; top: 50%; transform: translateY(-50%); background-color: rgba(255, 255, 255, 0.8); border: none; border-radius: 50%; width: 44px; height: 44px; cursor: pointer; font-size: 1.5rem; color: var(--color-secondary); box-shadow: var(--shadow-md); }
.carousel-btn.prev { left: 1rem; }
.carousel-btn.next { right: 1rem; }
.carousel-dots { text-align: center; margin-top: 1.5rem; }
.carousel-dot { display: inline-block; width: 12px; height: 12px; border-radius: 50%; background-color: #cbd5e1; margin: 0 5px; cursor: pointer; transition: background-color var(--transition-duration); }
.carousel-dot.active { background-color: var(--color-primary); }

/* Gallery & Lightbox */
.gallery-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 1rem; }
.gallery-item { cursor: pointer; overflow: hidden; border-radius: var(--border-radius-md); }
.gallery-image { width: 100%; height: 100%; object-fit: cover; transition: transform 0.4s ease; }
.gallery-item:hover .gallery-image { transform: scale(1.05); }

.lightbox { display: none; position: fixed; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(0, 0, 0, 0.9); z-index: 2000; justify-content: center; align-items: center; }
.lightbox.active { display: flex; }
.lightbox-content { position: relative; max-width: 90%; max-height: 90%; }
.lightbox-image { max-width: 100%; max-height: 100%; object-fit: contain; }
.lightbox-close, .lightbox-prev, .lightbox-next { position: absolute; background: none; border: none; color: white; font-size: 2.5rem; cursor: pointer; z-index: 2001; }
.lightbox-close { top: 20px; right: 30px; }
.lightbox-prev, .lightbox-next { top: 50%; transform: translateY(-50%); padding: 1rem; }
.lightbox-prev { left: 10px; }
.lightbox-next { right: 10px; }

/* Motion / Scroll Reveal */
.fade-in-up, .reveal-item {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s var(--transition-timing), transform 0.6s var(--transition-timing);
}
.reveal-item.is-visible, .fade-in-up.is-visible {
    opacity: 1;
    transform: translateY(0);
}

@media (prefers-reduced-motion: reduce) {
    html { scroll-behavior: auto; }
    .fade-in-up, .reveal-item, .btn, .feature-card, .service-card, .gallery-image {
        transition: none;
        opacity: 1;
        transform: none;
    }
}

/* Cookie Banner */
.cookie-banner { position: fixed; bottom: 0; left: 0; right: 0; background-color: var(--color-secondary); color: var(--color-text-light); padding: 1rem; display: flex; justify-content: center; align-items: center; gap: 1rem; z-index: 3000; transform: translateY(100%); transition: transform 0.5s ease-in-out; }
.cookie-banner.active { transform: translateY(0); }

/* Back to Top Button */
.back-to-top { position: fixed; bottom: 20px; right: 20px; width: 44px; height: 44px; background-color: var(--color-primary); color: white; border: none; border-radius: 50%; cursor: pointer; z-index: 1000; opacity: 0; visibility: hidden; transition: opacity 0.3s, visibility 0.3s; }
.back-to-top.visible { opacity: 1; visibility: visible; }
.back-to-top svg { width: 24px; height: 24px; fill: white; }

/* Sticky Context CTA */
.sticky-cta { position: fixed; bottom: 20px; left: 50%; transform: translateX(-50%); z-index: 999; opacity: 0; visibility: hidden; transition: opacity 0.3s, visibility 0.3s; }
.sticky-cta.visible { opacity: 1; visibility: visible; }

/* --- RESPONSIVE --- */
@media (max-width: 992px) {
    .main-nav, .header-cta { display: none; }
    .mobile-nav-toggle { display: block; }
    .mobile-nav-toggle span { display: block; width: 25px; height: 3px; background-color: var(--color-secondary); margin: 5px 0; transition: all 0.3s; }
    .mobile-nav-toggle.is-active span:nth-child(1) { transform: translateY(8px) rotate(45deg); }
    .mobile-nav-toggle.is-active span:nth-child(2) { opacity: 0; }
    .mobile-nav-toggle.is-active span:nth-child(3) { transform: translateY(-8px) rotate(-45deg); }

    .main-nav { position: fixed; top: 0; right: -100%; width: 80%; max-width: 300px; height: 100vh; background-color: var(--color-background); box-shadow: -5px 0 15px rgba(0,0,0,0.1); flex-direction: column; justify-content: center; align-items: center; transition: right 0.4s ease-in-out; }
    .main-nav.is-open { right: 0; display: flex; }
    .main-nav ul { flex-direction: column; text-align: center; }

    .hero-content { max-width: 100%; text-align: center; }
    .hero-cta-group { justify-content: center; }

    .about-teaser-grid, .service-item, .service-item.reverse, .about-main-grid, .contact-grid {
        grid-template-columns: 1fr;
    }
    .service-item.reverse .service-item-image { order: 0; }
}

@media (max-width: 576px) {
    :root { --container-padding: 1rem; }
    h1 { font-size: 2rem; }
    h2 { font-size: 1.75rem; }
    .hero { min-height: 70vh; padding: 4rem 0; }
    .hero-subheadline { font-size: 1.1rem; }
    .carousel-btn { width: 36px; height: 36px; font-size: 1.2rem; }
    .carousel-btn.prev { left: 0.5rem; }
    .carousel-btn.next { right: 0.5rem; }
    .sticky-cta { display: none; }
}

document.addEventListener('DOMContentLoaded', function() {

    // --- UTILITY FUNCTIONS ---
    const select = (el, all = false) => all ? document.querySelectorAll(el) : document.querySelector(el);

    // --- STICKY HEADER ---
    const header = select('.sticky-header');
    if (header) {
        const headerScroll = () => {
            window.scrollY > 50 ? header.classList.add('is-scrolled') : header.classList.remove('is-scrolled');
        };
        window.addEventListener('load', headerScroll);
        document.addEventListener('scroll', headerScroll);
    }

    // --- MOBILE NAVIGATION ---
    const mobileNavToggle = select('.mobile-nav-toggle');
    const mainNav = select('.main-nav');
    if (mobileNavToggle) {
        mobileNavToggle.addEventListener('click', () => {
            const isOpen = mainNav.classList.toggle('is-open');
            mobileNavToggle.classList.toggle('is-active');
            mobileNavToggle.setAttribute('aria-expanded', isOpen);
            document.body.style.overflow = isOpen ? 'hidden' : '';
        });
        // Close on click outside
        document.addEventListener('click', (e) => {
            if (mainNav.classList.contains('is-open') && !mainNav.contains(e.target) && !mobileNavToggle.contains(e.target)) {
                mainNav.classList.remove('is-open');
                mobileNavToggle.classList.remove('is-active');
                mobileNavToggle.setAttribute('aria-expanded', 'false');
                document.body.style.overflow = '';
            }
        });
    }

    // --- SCROLL REVEAL ANIMATIONS ---
    const revealItems = select('.reveal-item, .fade-in-up', true);
    const revealObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const delay = parseInt(entry.target.dataset.delay) || 0;
                setTimeout(() => {
                    entry.target.classList.add('is-visible');
                }, delay);
                observer.unobserve(entry.target);
            }
        });
    }, { threshold: 0.1 });
    revealItems.forEach(item => revealObserver.observe(item));

    // --- FAQ ACCORDION ---
    const faqItems = select('.faq-item', true);
    faqItems.forEach(item => {
        const question = item.querySelector('.faq-question');
        const answer = item.querySelector('.faq-answer');
        question.addEventListener('click', () => {
            const isExpanded = question.getAttribute('aria-expanded') === 'true';
            question.setAttribute('aria-expanded', !isExpanded);
            answer.style.maxHeight = isExpanded ? null : answer.scrollHeight + 'px';
        });
    });

    // --- CAROUSEL ---
    const carousel = select('.carousel');
    if (carousel) {
        const slides = select('.carousel-slide', true);
        const prevBtn = select('.carousel-btn.prev');
        const nextBtn = select('.carousel-btn.next');
        const dotsContainer = select('.carousel-dots');
        let currentIndex = 0;

        const createDots = () => {
            slides.forEach((_, i) => {
                const dot = document.createElement('button');
                dot.classList.add('carousel-dot');
                dot.addEventListener('click', () => goToSlide(i));
                dotsContainer.appendChild(dot);
            });
        };

        const updateCarousel = () => {
            carousel.style.transform = `translateX(-${currentIndex * 100}%)`;
            const dots = select('.carousel-dot', true);
            dots.forEach((dot, i) => dot.classList.toggle('active', i === currentIndex));
        };

        const goToSlide = (index) => {
            currentIndex = (index + slides.length) % slides.length;
            updateCarousel();
        };

        prevBtn.addEventListener('click', () => goToSlide(currentIndex - 1));
        nextBtn.addEventListener('click', () => goToSlide(currentIndex + 1));

        // Touch/Swipe functionality
        let touchStartX = 0;
        carousel.addEventListener('touchstart', e => touchStartX = e.touches[0].clientX);
        carousel.addEventListener('touchend', e => {
            const touchEndX = e.changedTouches[0].clientX;
            if (touchStartX - touchEndX > 50) nextBtn.click();
            if (touchStartX - touchEndX < -50) prevBtn.click();
        });

        // Make slides have same width as carousel container
        const resizeObserver = new ResizeObserver(entries => {
            for (let entry of entries) {
                const slideWidth = entry.contentRect.width;
                slides.forEach(slide => slide.style.flex = `0 0 ${slideWidth}px`);
            }
        });
        resizeObserver.observe(carousel.parentElement);

        createDots();
        goToSlide(0);
    }

    // --- GALLERY LIGHTBOX ---
    const galleryItems = select('.gallery-item', true);
    const lightbox = select('#lightbox');
    if (lightbox && galleryItems.length > 0) {
        const lightboxImg = select('.lightbox-image');
        const images = Array.from(galleryItems).map(item => item.querySelector('img').src);
        let currentIndex = 0;

        const showImage = (index) => {
            lightboxImg.src = images[index];
            currentIndex = index;
        };

        galleryItems.forEach((item, index) => {
            item.addEventListener('click', () => {
                lightbox.classList.add('active');
                showImage(index);
            });
        });

        const closeLightbox = () => lightbox.classList.remove('active');
        const prevImage = () => showImage((currentIndex - 1 + images.length) % images.length);
        const nextImage = () => showImage((currentIndex + 1) % images.length);

        select('.lightbox-close').addEventListener('click', closeLightbox);
        select('.lightbox-prev').addEventListener('click', prevImage);
        select('.lightbox-next').addEventListener('click', nextImage);
        lightbox.addEventListener('click', e => e.target === lightbox && closeLightbox());
        document.addEventListener('keydown', e => {
            if (!lightbox.classList.contains('active')) return;
            if (e.key === 'Escape') closeLightbox();
            if (e.key === 'ArrowLeft') prevImage();
            if (e.key === 'ArrowRight') nextImage();
        });
    }

    // --- COOKIE BANNER ---
    const cookieBanner = select('#cookie-banner');
    const cookieAccept = select('#cookie-accept');
    if (cookieBanner && !localStorage.getItem('cookieAccepted')) {
        setTimeout(() => cookieBanner.classList.add('active'), 2000);
        cookieAccept.addEventListener('click', () => {
            cookieBanner.classList.remove('active');
            localStorage.setItem('cookieAccepted', 'true');
        });
    }

    // --- BACK TO TOP BUTTON ---
    const backToTopButton = select('#back-to-top');
    if (backToTopButton) {
        const toggleVisibility = () => {
            window.scrollY > 300 ? backToTopButton.classList.add('visible') : backToTopButton.classList.remove('visible');
        };
        window.addEventListener('scroll', toggleVisibility);
        backToTopButton.addEventListener('click', () => window.scrollTo({ top: 0, behavior: 'smooth' }));
    }

    // --- STICKY CONTEXT CTA ---
    const stickyCTA = select('#sticky-cta');
    if (stickyCTA) {
        const toggleStickyCTA = () => {
            const footer = select('.site-footer');
            const footerRect = footer.getBoundingClientRect();
            const isVisible = window.scrollY > 400 && footerRect.top > window.innerHeight;
            stickyCTA.classList.toggle('visible', isVisible);
        };
        window.addEventListener('scroll', toggleStickyCTA);
    }

});
