:root {
    --color-primary: #007A7A;
    --color-primary-dark: #005F5F;
    --color-secondary: #C4E0E0;
    --color-accent: #F2A900;
    --color-neutral-0: #FFFFFF;
    --color-neutral-100: #F8F9FA;
    --color-neutral-700: #495057;
    --color-neutral-900: #212529;
    --font-family-display: 'Helvetica Neue', 'Arial', sans-serif;
    --font-family-body: 'Helvetica', 'Arial', sans-serif;
    --font-weight-regular: 400;
    --font-weight-bold: 700;
    --spacing-xs: 4px;
    --spacing-s: 8px;
    --spacing-m: 16px;
    --spacing-l: 24px;
    --spacing-xl: 32px;
    --spacing-xxl: 64px;
    --border-radius: 8px;
    --shadow-soft: 0 4px 12px rgba(0, 0, 0, 0.08);
    --shadow-medium: 0 8px 24px rgba(0, 0, 0, 0.12);
    --transition-speed: 0.3s;
    --section-gap: clamp(72px, 10vw, 120px);
    --container-width: 1200px;
}

*,
*::before,
*::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: var(--font-family-body);
    font-weight: var(--font-weight-regular);
    line-height: 1.6;
    color: var(--color-neutral-700);
    background-color: var(--color-neutral-0);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

h1, h2, h3, h4 {
    font-family: var(--font-family-display);
    font-weight: var(--font-weight-bold);
    color: var(--color-neutral-900);
    line-height: 1.2;
}

h1 { font-size: clamp(2.5rem, 5vw, 3.5rem); }
h2 { font-size: clamp(2rem, 4vw, 2.75rem); }
h3 { font-size: 1.5rem; }
h4 { font-size: 1.25rem; }

p {
    margin-bottom: var(--spacing-m);
    max-width: 65ch;
}

a {
    color: var(--color-primary);
    text-decoration: none;
    transition: color var(--transition-speed) ease;
}

a:hover, a:focus {
    color: var(--color-primary-dark);
    text-decoration: underline;
}

ul {
    list-style: none;
}

img {
    max-width: 100%;
    height: auto;
    display: block;
}

.container {
    max-width: var(--container-width);
    margin-left: auto;
    margin-right: auto;
    padding-left: var(--spacing-l);
    padding-right: var(--spacing-l);
}

section {
    padding-top: var(--section-gap);
    padding-bottom: var(--section-gap);
}

.btn {
    display: inline-block;
    padding: 12px 24px;
    font-weight: var(--font-weight-bold);
    text-align: center;
    border-radius: var(--border-radius);
    cursor: pointer;
    transition: transform var(--transition-speed) ease, box-shadow var(--transition-speed) ease, background-color var(--transition-speed) ease;
    min-height: 44px;
    border: 2px solid transparent;
}

.btn-primary {
    background-color: var(--color-primary);
    color: var(--color-neutral-0);
    border-color: var(--color-primary);
}

.btn-primary:hover, .btn-primary:focus {
    background-color: var(--color-primary-dark);
    border-color: var(--color-primary-dark);
    color: var(--color-neutral-0);
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
    text-decoration: none;
}

.btn-secondary {
    background-color: transparent;
    color: var(--color-primary);
    border-color: var(--color-primary);
}

.btn-secondary:hover, .btn-secondary:focus {
    background-color: var(--color-secondary);
    color: var(--color-primary-dark);
    border-color: var(--color-primary-dark);
    transform: translateY(-2px);
    box-shadow: var(--shadow-soft);
    text-decoration: none;
}

/* Header */
.site-header {
    position: sticky;
    top: 0;
    z-index: 1000;
    background-color: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    padding: var(--spacing-m) 0;
    transition: padding var(--transition-speed) ease, box-shadow var(--transition-speed) ease;
}

.site-header.scrolled {
    padding: var(--spacing-s) 0;
    box-shadow: var(--shadow-soft);
}

.site-header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 1.25rem;
    font-weight: var(--font-weight-bold);
    color: var(--color-neutral-900);
}
.logo:hover, .logo:focus { text-decoration: none; color: var(--color-primary); }

.main-nav ul {
    display: flex;
    gap: var(--spacing-l);
}

.main-nav a {
    color: var(--color-neutral-700);
    font-weight: var(--font-weight-bold);
    padding: var(--spacing-s) 0;
    position: relative;
}

.main-nav a::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--color-primary);
    transition: width var(--transition-speed) ease;
}

.main-nav a:hover::after, .main-nav a:focus::after, .main-nav a.active::after {
    width: 100%;
}

.main-nav a.active {
    color: var(--color-primary);
}

.mobile-nav-toggle {
    display: none;
    background: none;
    border: none;
    cursor: pointer;
    z-index: 1001;
}

.mobile-nav-toggle span {
    display: block;
    width: 25px;
    height: 3px;
    background-color: var(--color-neutral-900);
    margin: 5px 0;
    transition: transform 0.3s, opacity 0.3s;
}

/* Hero Section */
.hero {
    position: relative;
    height: 70vh;
    min-height: 500px;
    display: flex;
    align-items: center;
    color: var(--color-neutral-0);
    padding: 0;
}

.hero-bg-image {
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background-size: cover;
    background-position: center;
    z-index: -2;
}

.hero::before {
    content: '';
    position: absolute;
    top: 0; left: 0; right: 0; bottom: 0;
    background: linear-gradient(45deg, rgba(0, 122, 122, 0.7), rgba(0, 95, 95, 0.5));
    z-index: -1;
}

.hero-content .container {
    max-width: 800px;
}

.hero h1 {
    color: var(--color-neutral-0);
    margin-bottom: var(--spacing-m);
}

.hero p {
    font-size: 1.25rem;
    margin-bottom: var(--spacing-l);
    max-width: 60ch;
}

.cta-group {
    display: flex;
    gap: var(--spacing-m);
}

.cta-group .btn-primary { background-color: var(--color-accent); border-color: var(--color-accent); color: var(--color-neutral-900); }
.cta-group .btn-primary:hover, .cta-group .btn-primary:focus { background-color: #d99800; border-color: #d99800; }
.cta-group .btn-secondary { background-color: transparent; border-color: var(--color-neutral-0); color: var(--color-neutral-0); }
.cta-group .btn-secondary:hover, .cta-group .btn-secondary:focus { background-color: rgba(255,255,255,0.2); }

/* USP Section */
.usp-section { background-color: var(--color-neutral-100); }
.usp-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: var(--spacing-xl);
}
.usp-card { text-align: center; }
.usp-card h3 { margin-bottom: var(--spacing-s); }

/* Wave Divider */
.wave-divider {
    width: 100%;
    height: 100px;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none"><path d="M321.39,56.44c58-10.79,114.16-30.13,172-41.86,82.39-16.72,168.19-17.73,250.45-.39C823.78,31.74,906.67,72,985.66,92.83c70.05,18.48,146.53,26.09,214.34,3V0H0V27.35A600.21,600.21,0,0,0,321.39,56.44Z" style="fill:%23f8f9fa;"></path></svg>');
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center bottom;
}
.wave-divider.reverse { transform: scaleY(-1); }

/* Services Overview */
.section-title { text-align: center; margin-bottom: var(--spacing-s); }
.section-subtitle { text-align: center; max-width: 60ch; margin: 0 auto var(--spacing-xl) auto; }

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--spacing-xl);
}

.service-card {
    display: block;
    background-color: var(--color-neutral-0);
    border-radius: var(--border-radius);
    overflow: hidden;
    box-shadow: var(--shadow-soft);
    transition: transform var(--transition-speed) ease, box-shadow var(--transition-speed) ease;
}

.service-card:hover, .service-card:focus {
    transform: translateY(-5px);
    box-shadow: var(--shadow-medium);
    text-decoration: none;
}

.service-card img { width: 100%; height: 200px; object-fit: cover; }
.service-card-content { padding: var(--spacing-l); }
.service-card h4 { margin-bottom: var(--spacing-s); }
.service-card p { margin-bottom: var(--spacing-m); color: var(--color-neutral-700); }
.learn-more { font-weight: var(--font-weight-bold); color: var(--color-primary); }

/* About Short Section */
.about-short-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-xxl);
    align-items: center;
}

.about-image img {
    border-radius: var(--border-radius);
    box-shadow: var(--shadow-medium);
}

/* Testimonials / Carousel */
.testimonials { background-color: var(--color-neutral-100); }
.carousel-wrapper { position: relative; }
.carousel { display: flex; overflow: hidden; }
.carousel-slide { flex: 0 0 100%; }
.carousel-slide img { width: 100%; height: 500px; object-fit: cover; border-radius: var(--border-radius); }

.carousel-btn {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background-color: rgba(255, 255, 255, 0.8);
    border: none;
    border-radius: 50%;
    width: 44px;
    height: 44px;
    font-size: 1.5rem;
    cursor: pointer;
    box-shadow: var(--shadow-soft);
}
.carousel-btn.prev { left: var(--spacing-m); }
.carousel-btn.next { right: var(--spacing-m); }

.carousel-dots {
    position: absolute;
    bottom: var(--spacing-m);
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: var(--spacing-s);
}

.carousel-dot {
    width: 12px; height: 12px;
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 50%;
    cursor: pointer;
    transition: background-color var(--transition-speed) ease;
}
.carousel-dot.active { background-color: var(--color-neutral-0); }

/* Contact CTA */
.contact-cta { background-color: var(--color-primary); color: var(--color-neutral-0); }
.cta-box { text-align: center; }
.cta-box h2 { color: var(--color-neutral-0); margin-bottom: var(--spacing-m); }
.cta-box p { color: var(--color-secondary); margin: 0 auto var(--spacing-l) auto; }
.cta-box .btn-primary { background-color: var(--color-accent); border-color: var(--color-accent); color: var(--color-neutral-900); }
.cta-box .btn-primary:hover, .cta-box .btn-primary:focus { background-color: #d99800; border-color: #d99800; }

/* Footer */
.site-footer { background-color: var(--color-neutral-900); color: var(--color-neutral-100); padding-top: var(--spacing-xxl); padding-bottom: var(--spacing-l); }
.footer-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: var(--spacing-xl); margin-bottom: var(--spacing-xl); }
.footer-col h4 { color: var(--color-neutral-0); margin-bottom: var(--spacing-m); }
.footer-col p, .footer-col a { color: var(--color-neutral-100); font-size: 0.9rem; }
.footer-col a:hover, .footer-col a:focus { color: var(--color-secondary); }
.footer-col ul { display: flex; flex-direction: column; gap: var(--spacing-s); }
.footer-bottom { text-align: center; border-top: 1px solid var(--color-neutral-700); padding-top: var(--spacing-l); font-size: 0.8rem; color: var(--color-neutral-100); }

/* Page Specific Styles */
.page-header { text-align: center; padding-bottom: 0; }
.legal-content { padding-top: 0; }
.legal-content h2 { margin-top: var(--spacing-xl); margin-bottom: var(--spacing-m); }

/* Services Detailed */
.service-item {
    display: grid;
    grid-template-columns: 1fr 1.5fr;
    gap: var(--spacing-xxl);
    align-items: center;
    margin-bottom: var(--section-gap);
}
.service-item.reverse { grid-template-columns: 1.5fr 1fr; }
.service-item.reverse .service-item-image { grid-column: 2; grid-row: 1; }
.service-item.reverse .service-item-content { grid-column: 1; grid-row: 1; }
.service-item-image img { border-radius: var(--border-radius); box-shadow: var(--shadow-medium); }

/* Praxis & Team */
.team-intro {
    display: grid;
    grid-template-columns: 300px 1fr;
    gap: var(--spacing-xl);
    align-items: center;
}
.team-intro-image img { border-radius: 50%; }

/* Gallery */
.gallery-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--spacing-l);
}
.gallery-item img {
    width: 100%;
    height: 250px;
    object-fit: cover;
    border-radius: var(--border-radius);
    cursor: pointer;
    transition: transform 0.3s, box-shadow 0.3s;
}
.gallery-item img:hover { transform: scale(1.03); box-shadow: var(--shadow-medium); }

/* Lightbox */
.lightbox { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.8); display: none; justify-content: center; align-items: center; z-index: 2000; }
.lightbox.active { display: flex; }
.lightbox-content { position: relative; max-width: 90%; max-height: 90%; }
#lightbox-img { max-width: 100%; max-height: 100%; border-radius: var(--border-radius); }
.lightbox-close { position: absolute; top: -40px; right: 0; font-size: 2rem; color: #fff; background: none; border: none; cursor: pointer; }
.lightbox-prev, .lightbox-next { position: absolute; top: 50%; transform: translateY(-50%); font-size: 2rem; color: #fff; background: rgba(0,0,0,0.5); border: none; cursor: pointer; padding: 10px 15px; border-radius: 5px; }
.lightbox-prev { left: -60px; }
.lightbox-next { right: -60px; }

/* FAQ */
.faq-accordion { max-width: 800px; margin: 0 auto; }
.faq-item { border-bottom: 1px solid var(--color-secondary); }
.faq-question { width: 100%; background: none; border: none; text-align: left; padding: var(--spacing-l) 0; font-size: 1.1rem; font-weight: var(--font-weight-bold); cursor: pointer; display: flex; justify-content: space-between; align-items: center; }
.faq-icon { width: 1em; height: 1em; transition: transform 0.3s; position: relative; }
.faq-icon::before, .faq-icon::after { content: ''; position: absolute; top: 50%; left: 50%; width: 100%; height: 2px; background: var(--color-primary); transform: translate(-50%, -50%); }
.faq-icon::after { transform: translate(-50%, -50%) rotate(90deg); transition: transform 0.3s; }
.faq-question[aria-expanded="true"] .faq-icon::after { transform: translate(-50%, -50%) rotate(0deg); }
.faq-answer { max-height: 0; overflow: hidden; transition: max-height 0.3s ease-out; }
.faq-answer p { padding: 0 0 var(--spacing-l) 0; }

/* Contact */
.contact-grid { display: grid; grid-template-columns: 1fr 1.5fr; gap: var(--spacing-xxl); }
.form-group { margin-bottom: var(--spacing-l); }
.form-group label { display: block; margin-bottom: var(--spacing-s); font-weight: var(--font-weight-bold); }
.form-group input, .form-group textarea { width: 100%; padding: 12px; border: 1px solid var(--color-secondary); border-radius: var(--border-radius); }
.form-group input:focus, .form-group textarea:focus { outline: none; border-color: var(--color-primary); box-shadow: 0 0 0 3px rgba(0, 122, 122, 0.2); }
.checkbox-label { display: inline; font-weight: normal; margin-left: var(--spacing-s); }

/* Cookie Banner */
.cookie-banner { position: fixed; bottom: 0; left: 0; right: 0; background: var(--color-neutral-900); color: var(--color-neutral-0); padding: var(--spacing-m); display: flex; justify-content: center; align-items: center; gap: var(--spacing-m); z-index: 1500; transform: translateY(100%); transition: transform 0.5s ease-in-out; }
.cookie-banner.visible { transform: translateY(0); }
.cookie-banner a { color: var(--color-secondary); }

/* Sticky CTA */
.sticky-cta { position: fixed; bottom: 20px; right: 20px; z-index: 900; opacity: 0; transform: translateY(20px); transition: opacity 0.3s, transform 0.3s; pointer-events: none; }
.sticky-cta.visible { opacity: 1; transform: translateY(0); pointer-events: auto; }

/* Motion & Animations */
.fade-in-up {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s ease-out, transform 0.6s ease-out;
}
.fade-in-up.visible {
    opacity: 1;
    transform: translateY(0);
}

@media (prefers-reduced-motion: reduce) {
    html { scroll-behavior: auto; }
    .fade-in-up, .btn, .service-card, .gallery-item img, .faq-icon, .sticky-cta, .site-header {
        transition: none !important;
        opacity: 1 !important;
        transform: none !important;
    }
}

/* Responsive */
@media (max-width: 992px) {
    .about-short-content, .service-item, .service-item.reverse, .contact-grid {
        grid-template-columns: 1fr;
    }
    .service-item.reverse .service-item-image { grid-column: 1; }
    .team-intro { grid-template-columns: 1fr; text-align: center; }
    .team-intro-image { margin: 0 auto; width: 200px; height: 200px; }
}

@media (max-width: 768px) {
    .cta-header { display: none; }
    .main-nav { position: fixed; top: 0; right: -100%; width: 300px; height: 100vh; background: var(--color-neutral-0); box-shadow: var(--shadow-medium); transition: right 0.3s ease-in-out; display: flex; align-items: center; justify-content: center; }
    .main-nav.open { right: 0; }
    .main-nav ul { flex-direction: column; text-align: center; }
    .mobile-nav-toggle { display: block; }
    .mobile-nav-toggle.open span:nth-child(1) { transform: translateY(8px) rotate(45deg); }
    .mobile-nav-toggle.open span:nth-child(2) { opacity: 0; }
    .mobile-nav-toggle.open span:nth-child(3) { transform: translateY(-8px) rotate(-45deg); }

    .hero { height: 80vh; }
    .cta-group { flex-direction: column; align-items: flex-start; }
    .footer-grid { text-align: center; }
    .lightbox-prev { left: 10px; }
    .lightbox-next { right: 10px; }
}

document.addEventListener('DOMContentLoaded', () => {

    // --- Sticky Header --- //
    const header = document.querySelector('.site-header');
    if (header) {
        window.addEventListener('scroll', () => {
            if (window.scrollY > 50) {
                header.classList.add('scrolled');
            } else {
                header.classList.remove('scrolled');
            }
        });
    }

    // --- Mobile Navigation --- //
    const navToggle = document.querySelector('.mobile-nav-toggle');
    const mainNav = document.querySelector('.main-nav');
    if (navToggle && mainNav) {
        navToggle.addEventListener('click', () => {
            const isOpen = navToggle.getAttribute('aria-expanded') === 'true';
            navToggle.setAttribute('aria-expanded', !isOpen);
            navToggle.classList.toggle('open');
            mainNav.classList.toggle('open');
        });
    }

    // --- Scroll Reveal Animation --- //
    const animatedElements = document.querySelectorAll('.fade-in-up');
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const delay = parseInt(entry.target.dataset.delay) || 0;
                setTimeout(() => {
                    entry.target.classList.add('visible');
                }, delay);
                observer.unobserve(entry.target);
            }
        });
    }, { threshold: 0.1 });

    animatedElements.forEach(el => {
        observer.observe(el);
    });

    // --- Carousel --- //
    const carousel = document.querySelector('.carousel');
    if (carousel) {
        const slides = carousel.querySelectorAll('.carousel-slide');
        const prevBtn = document.querySelector('.carousel-btn.prev');
        const nextBtn = document.querySelector('.carousel-btn.next');
        const dotsContainer = document.querySelector('.carousel-dots');
        let currentIndex = 0;

        const goToSlide = (index) => {
            carousel.style.transform = `translateX(-${index * 100}%)`;
            updateDots(index);
            currentIndex = index;
        };

        const updateDots = (index) => {
            const dots = dotsContainer.querySelectorAll('.carousel-dot');
            dots.forEach((dot, i) => {
                dot.classList.toggle('active', i === index);
            });
        };

        slides.forEach((_, i) => {
            const dot = document.createElement('button');
            dot.classList.add('carousel-dot');
            dot.addEventListener('click', () => goToSlide(i));
            dotsContainer.appendChild(dot);
        });

        prevBtn.addEventListener('click', () => {
            const newIndex = (currentIndex - 1 + slides.length) % slides.length;
            goToSlide(newIndex);
        });

        nextBtn.addEventListener('click', () => {
            const newIndex = (currentIndex + 1) % slides.length;
            goToSlide(newIndex);
        });

        goToSlide(0);
    }

    // --- Gallery Lightbox --- //
    const lightbox = document.getElementById('lightbox');
    const lightboxImg = document.getElementById('lightbox-img');
    const galleryItems = document.querySelectorAll('.lightbox-trigger');
    let currentImageIndex;

    if (lightbox && lightboxImg && galleryItems.length > 0) {
        const images = Array.from(galleryItems).map(item => item.src);

        const openLightbox = (index) => {
            currentImageIndex = index;
            lightboxImg.src = images[currentImageIndex];
            lightbox.classList.add('active');
        };

        const closeLightbox = () => {
            lightbox.classList.remove('active');
        };

        const showNextImage = () => {
            currentImageIndex = (currentImageIndex + 1) % images.length;
            lightboxImg.src = images[currentImageIndex];
        };

        const showPrevImage = () => {
            currentImageIndex = (currentImageIndex - 1 + images.length) % images.length;
            lightboxImg.src = images[currentImageIndex];
        };

        galleryItems.forEach((item, index) => {
            item.addEventListener('click', () => openLightbox(index));
        });

        lightbox.addEventListener('click', (e) => {
            if (e.target === lightbox || e.target.classList.contains('lightbox-close')) {
                closeLightbox();
            }
        });

        document.querySelector('.lightbox-next').addEventListener('click', showNextImage);
        document.querySelector('.lightbox-prev').addEventListener('click', showPrevImage);

        document.addEventListener('keydown', (e) => {
            if (lightbox.classList.contains('active')) {
                if (e.key === 'Escape') closeLightbox();
                if (e.key === 'ArrowRight') showNextImage();
                if (e.key === 'ArrowLeft') showPrevImage();
            }
        });
    }

    // --- FAQ Accordion --- //
    const faqItems = document.querySelectorAll('.faq-item');
    faqItems.forEach(item => {
        const question = item.querySelector('.faq-question');
        const answer = item.querySelector('.faq-answer');

        question.addEventListener('click', () => {
            const isExpanded = question.getAttribute('aria-expanded') === 'true';
            question.setAttribute('aria-expanded', !isExpanded);
            if (!isExpanded) {
                answer.style.maxHeight = answer.scrollHeight + 'px';
            } else {
                answer.style.maxHeight = '0';
            }
        });
    });

    // --- Cookie Banner --- //
    const cookieBanner = document.getElementById('cookie-banner');
    const cookieAccept = document.getElementById('cookie-accept');

    if (cookieBanner && cookieAccept) {
        if (!localStorage.getItem('cookiesAccepted')) {
            setTimeout(() => {
                cookieBanner.classList.add('visible');
            }, 1000);
        }

        cookieAccept.addEventListener('click', () => {
            localStorage.setItem('cookiesAccepted', 'true');
            cookieBanner.classList.remove('visible');
        });
    }

    // --- Sticky CTA --- //
    const stickyCta = document.getElementById('sticky-cta');
    if (stickyCta) {
        const ctaObserver = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                // Show when the hero section is NOT intersecting (i.e., scrolled past it)
                if (!entry.isIntersecting) {
                    stickyCta.classList.add('visible');
                } else {
                    stickyCta.classList.remove('visible');
                }
            });
        }, { threshold: 0.1 });

        const heroSection = document.querySelector('.hero');
        if (heroSection) {
            ctaObserver.observe(heroSection);
        }
    }
});
