@import url('https://fonts.googleapis.com/css2?family=Lora:ital,wght@0,400..700;1,400..700&family=Sora:wght@100..800&display=swap');

:root {
    --bg: hsl(40, 33%, 98%);
    --surface: hsl(40, 25%, 94%);
    --text: #78716c;
    --muted: hsl(32, 7%, 55%);
    --primary: #14532d;
    --accent: #16a34a;
    --accent2: #facc15;
    --border: hsl(32, 9%, 85%);

    --radius-sm: 8px;
    --radius-md: 16px;
    --radius-lg: 24px;

    --shadow-soft: 0 4px 12px hsla(30, 10%, 20%, 0.05);
    --shadow-lift: 0 10px 25px hsla(30, 10%, 20%, 0.08);

    --spacing-xs: 4px;
    --spacing-sm: 8px;
    --spacing-md: 16px;
    --spacing-lg: 32px;
    --spacing-xl: 48px;
    --spacing-xxl: 80px;
    --spacing-xxxl: 120px;

    --font-body: 'Sora', sans-serif;
    --font-display: 'Lora', serif;

    --h1: clamp(2.5rem, 5vw + 1rem, 4.5rem);
    --h2: clamp(2.0rem, 4vw + 0.8rem, 3.5rem);
    --h3: clamp(1.5rem, 2.5vw + 0.6rem, 2.25rem);
    --body: clamp(1rem, 0.5vw + 0.9rem, 1.125rem);
    --body-large: clamp(1.1rem, 0.6vw + 1rem, 1.25rem);
    --caption: clamp(0.875rem, 0.2vw + 0.8rem, 1rem);

    --max-width: 1280px;
    --section-padding: clamp(4rem, 10vw, 7.5rem);
}

/* --- BASE & RESET --- */
*, *::before, *::after { box-sizing: border-box; }
body { margin: 0; font-family: var(--font-body); font-size: var(--body); line-height: 1.7; color: var(--text); background-color: var(--bg); -webkit-font-smoothing: antialiased; }
img, picture, video, canvas, svg { display: block; max-width: 100%; }
input, button, textarea, select { font: inherit; }
p, h1, h2, h3, h4, h5, h6 { overflow-wrap: break-word; margin: 0; }
ul { list-style: none; padding: 0; margin: 0; }
a { color: var(--primary); text-decoration: none; transition: color 0.2s ease; }
a:hover { color: var(--accent); }

/* --- TYPOGRAPHY --- */
h1, h2, h3 { font-family: var(--font-display); color: var(--primary); font-weight: 600; line-height: 1.2; }
h1 { font-size: var(--h1); }
h2 { font-size: var(--h2); margin-bottom: var(--spacing-md); }
h3 { font-size: var(--h3); margin-bottom: var(--spacing-sm); }
.kicker { font-family: var(--font-body); font-size: var(--caption); color: var(--accent); font-weight: 600; text-transform: uppercase; letter-spacing: 1px; margin-bottom: var(--spacing-md); }
.subline { font-size: var(--body-large); color: var(--muted); max-width: 65ch; }
.text-container { max-width: 75ch; margin-left: auto; margin-right: auto; }
.text-container p, .text-container ul { margin-bottom: 1.5em; }
.text-container ul { list-style: disc; padding-left: 20px; }

/* --- LAYOUT & SECTIONS --- */
.container { width: 100%; max-width: var(--max-width); margin: 0 auto; padding: 0 var(--spacing-lg); }
section { padding: var(--section-padding) 0; }
.alternate-bg { background-color: var(--surface); }
.section-header { text-align: center; margin-bottom: var(--spacing-xl); }
.section-header h2 { margin-bottom: var(--spacing-sm); }
.section-header p { max-width: 65ch; margin: 0 auto; color: var(--muted); }

/* --- HEADER & NAVIGATION --- */
.site-header { position: sticky; top: 0; z-index: 1000; width: 100%; padding: var(--spacing-md) 0; transition: all 0.3s ease; background-color: hsla(40, 33%, 98%, 0.8); }
.site-header.scrolled { padding: var(--spacing-sm) 0; box-shadow: var(--shadow-soft); backdrop-filter: blur(8px); -webkit-backdrop-filter: blur(8px); }
.site-header .container { display: flex; justify-content: space-between; align-items: center; }
.logo { font-family: var(--font-display); font-weight: 700; font-size: 1.5rem; color: var(--primary); }
.main-nav ul { display: flex; gap: var(--spacing-lg); }
.main-nav a { font-weight: 500; padding: var(--spacing-sm) 0; position: relative; }
.main-nav a::after { content: ''; position: absolute; bottom: 0; left: 0; width: 0; height: 2px; background-color: var(--accent); transition: width 0.3s ease; }
.main-nav a:hover::after, .main-nav a.active::after { width: 100%; }
.mobile-nav-toggle { display: none; background: none; border: none; cursor: pointer; padding: 10px; z-index: 1001; }
.mobile-nav-toggle .icon-bar { display: block; width: 25px; height: 3px; background-color: var(--primary); margin: 5px 0; transition: all 0.3s ease; }
.mobile-nav-toggle.open .icon-bar:nth-child(1) { transform: translateY(8px) rotate(45deg); }
.mobile-nav-toggle.open .icon-bar:nth-child(2) { opacity: 0; }
.mobile-nav-toggle.open .icon-bar:nth-child(3) { transform: translateY(-8px) rotate(-45deg); }

/* --- BUTTONS --- */
.button { display: inline-block; padding: 12px 24px; border-radius: var(--radius-sm); font-weight: 600; text-align: center; cursor: pointer; transition: all 0.2s ease-out; border: 2px solid transparent; min-height: 44px; }
.primary-button { background-color: var(--primary); color: white; }
.primary-button:hover { background-color: var(--accent); transform: translateY(-2px); box-shadow: var(--shadow-lift); }
.outline-button { border-color: var(--primary); color: var(--primary); }
.outline-button:hover { background-color: var(--primary); color: white; transform: translateY(-2px); box-shadow: var(--shadow-lift); }
.large-button { padding: 16px 32px; font-size: 1.1rem; }

/* --- HERO SECTION --- */
.hero { padding: var(--section-padding) 0; }
.hero-container { display: grid; grid-template-columns: 1fr 1fr; align-items: center; gap: var(--spacing-xxl); }
.hero-content h1 { margin-bottom: var(--spacing-lg); }
.hero-content .subline { margin-bottom: var(--spacing-xl); }
.hero-ctas { display: flex; gap: var(--spacing-md); margin-bottom: var(--spacing-xl); }
.trust-chips { display: flex; flex-wrap: wrap; gap: var(--spacing-md); }
.trust-chips span { background-color: var(--surface); padding: var(--spacing-sm) var(--spacing-md); border-radius: var(--radius-sm); font-size: var(--caption); font-weight: 500; }
.hero-image-wrapper img { width: 100%; height: auto; object-fit: cover; border-radius: var(--radius-lg); box-shadow: var(--shadow-lift); }
.hero-subpage { padding: calc(var(--section-padding)/2) 0; text-align: center; background-size: cover; background-position: center; color: white; }
.hero-subpage h1, .hero-subpage .kicker, .hero-subpage .subline { color: white; }
.hero-container-subpage .subline { max-width: 65ch; margin: 0 auto; opacity: 0.9; }

/* --- SIGNATURE DIVIDER --- */
.signature-divider { line-height: 0; margin-top: calc(var(--section-padding) * -1 + 1px); position: relative; z-index: 1; }
.signature-divider svg { width: 100%; height: auto; }

/* --- FEATURE GRID --- */
.feature-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: var(--spacing-xl); }
.feature-card { text-align: center; }
.feature-icon { width: 48px; height: 48px; margin: 0 auto var(--spacing-md); background-color: var(--primary); -webkit-mask-size: contain; mask-size: contain; -webkit-mask-repeat: no-repeat; mask-repeat: no-repeat; -webkit-mask-position: center; mask-position: center; }
.heart-icon { -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/></svg>'); mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/></svg>');}
.award-icon { -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/></svg>'); mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/></svg>');}
.home-icon { -webkit-mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/></svg>'); mask-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="white"><path d="M10 20v-6h4v6h5v-8h3L12 3 2 12h3v8z"/></svg>');}

/* --- CARD GRID / SERVICES TEASER --- */
.card-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: var(--spacing-xl); }
.service-card { display: block; background-color: white; padding: var(--spacing-xl); border-radius: var(--radius-md); box-shadow: var(--shadow-soft); transition: all 0.2s ease-out; }
.service-card:hover { transform: translateY(-4px) scale(1.01); box-shadow: var(--shadow-lift); }
.service-card h3 { margin-bottom: var(--spacing-md); }
.card-link { display: inline-block; margin-top: var(--spacing-lg); font-weight: 600; color: var(--accent); }

/* --- TEXT WITH IMAGE --- */
.text-image-container { display: grid; grid-template-columns: 1fr 1fr; gap: var(--spacing-xxl); align-items: center; }
.text-image-container.image-left { grid-template-columns: 1fr 1fr; }
.text-image-container.image-left .image-content { grid-column: 1; grid-row: 1; }
.text-image-container.image-left .text-content { grid-column: 2; grid-row: 1; }
.text-with-image-section .image-content img { border-radius: var(--radius-lg); }

/* --- CAROUSEL --- */
.carousel-wrapper { position: relative; }
.carousel-slides { display: flex; overflow-x: auto; scroll-snap-type: x mandatory; -webkit-overflow-scrolling: touch; scrollbar-width: none; }
.carousel-slides::-webkit-scrollbar { display: none; }
.carousel-slide { flex: 0 0 100%; scroll-snap-align: center; padding: 0 var(--spacing-md); }
.carousel-slide img { border-radius: var(--radius-lg); width: 100%; aspect-ratio: 16/9; object-fit: cover; }
.carousel-caption { text-align: center; margin-top: var(--spacing-md); font-weight: 500; }
.carousel-controls { display: flex; justify-content: center; align-items: center; margin-top: var(--spacing-lg); gap: var(--spacing-md); }
.carousel-button { background: var(--surface); border: 1px solid var(--border); color: var(--primary); width: 44px; height: 44px; border-radius: 50%; cursor: pointer; font-size: 1.5rem; }
.carousel-dots { display: flex; gap: var(--spacing-sm); }
.carousel-dots .dot { width: 12px; height: 12px; border-radius: 50%; background: var(--border); cursor: pointer; }
.carousel-dots .dot.active { background: var(--primary); }

/* --- FAQ --- */
.faq-accordion { max-width: 800px; margin: 0 auto; }
.faq-item { border-bottom: 1px solid var(--border); }
.faq-question { width: 100%; background: none; border: none; text-align: left; padding: var(--spacing-lg) 0; font-size: 1.2rem; font-weight: 600; color: var(--primary); cursor: pointer; display: flex; justify-content: space-between; align-items: center; }
.faq-answer { max-height: 0; overflow: hidden; transition: max-height 0.3s ease-out; }
.faq-answer p { padding: 0 0 var(--spacing-lg) 0; }
.faq-icon { width: 20px; height: 20px; transition: transform 0.3s ease; }
.faq-icon::before, .faq-icon::after { content: ''; position: absolute; background-color: var(--primary); width: 16px; height: 2px; }
.faq-icon::after { transform: rotate(90deg); }
.faq-question[aria-expanded="true"] .faq-icon { transform: rotate(45deg); }
.faq-cta { text-align: center; margin-top: var(--spacing-xl); }
.faq-page-section .faq-category { margin-bottom: var(--spacing-xxl); }
.faq-page-section h3 { text-align: center; margin-bottom: var(--spacing-xl); }

/* --- LOCATION & CONTACT --- */
.location-container { display: grid; grid-template-columns: 1fr 1.5fr; gap: var(--spacing-xl); align-items: stretch; }
.location-card { background-color: var(--surface); padding: var(--spacing-xl); border-radius: var(--radius-lg); }
.location-details { display: flex; gap: var(--spacing-lg); margin: var(--spacing-lg) 0; }
.map-container { height: 100%; min-height: 400px; }
.contact-grid { display: grid; grid-template-columns: 1.5fr 1fr; gap: var(--spacing-xxl); }
.contact-form .form-group { margin-bottom: var(--spacing-lg); }
.contact-form label { display: block; font-weight: 500; margin-bottom: var(--spacing-sm); }
.contact-form input[type="text"], .contact-form input[type="email"], .contact-form textarea { width: 100%; padding: 12px; border: none; border-bottom: 2px solid var(--border); background-color: transparent; transition: border-color 0.2s ease; }
.contact-form input:focus, .contact-form textarea:focus { outline: none; border-color: var(--primary); }
.checkbox-group { display: flex; align-items: flex-start; gap: var(--spacing-sm); }
.checkbox-group input { margin-top: 5px; }
.checkbox-group label { font-size: var(--caption); }

/* --- CTA FOOTER --- */
.cta-footer-section { background-color: var(--primary); color: white; }
.cta-footer-container { text-align: center; }
.cta-footer-container h2, .cta-footer-container p { color: white; }
.cta-footer-container h2 { margin-bottom: var(--spacing-md); }
.cta-footer-container p { max-width: 60ch; margin: 0 auto var(--spacing-xl); opacity: 0.9; }
.cta-footer-container .primary-button { background-color: white; color: var(--primary); }
.cta-footer-container .primary-button:hover { background-color: var(--surface); }

/* --- FOOTER --- */
.site-footer { background-color: var(--surface); padding-top: var(--spacing-xxl); padding-bottom: var(--spacing-lg); }
.footer-container { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: var(--spacing-xl); }
.footer-col h4 { color: var(--primary); margin-bottom: var(--spacing-md); }
.footer-col ul li { margin-bottom: var(--spacing-sm); }
.footer-bottom { text-align: center; margin-top: var(--spacing-xl); padding-top: var(--spacing-lg); border-top: 1px solid var(--border); font-size: var(--caption); color: var(--muted); }

/* --- PREMIUM FEATURES --- */
.sticky-cta { position: fixed; bottom: 0; left: 0; width: 100%; background-color: hsla(40, 33%, 98%, 0.9); backdrop-filter: blur(8px); padding: var(--spacing-md) 0; box-shadow: 0 -4px 12px hsla(30, 10%, 20%, 0.05); z-index: 998; transform: translateY(100%); transition: transform 0.4s ease-out; }
.sticky-cta.visible { transform: translateY(0); }
.sticky-cta .container { display: flex; justify-content: space-between; align-items: center; }
.sticky-cta p { margin: 0; font-weight: 500; }

.cookie-banner { position: fixed; bottom: 0; left: 0; width: 100%; background-color: hsla(40, 25%, 94%, 0.9); backdrop-filter: blur(8px); padding: var(--spacing-md) 0; z-index: 2000; display: none; }
.cookie-banner.visible { display: block; }
.cookie-banner .container { display: flex; justify-content: space-between; align-items: center; gap: var(--spacing-lg); }
.cookie-banner p { margin: 0; }

.back-to-top { position: fixed; bottom: 20px; right: 20px; width: 50px; height: 50px; background: var(--primary); color: white; border: none; border-radius: 50%; cursor: pointer; z-index: 999; opacity: 0; visibility: hidden; transition: opacity 0.3s, visibility 0.3s; font-size: 1.5rem; display: flex; justify-content: center; align-items: center; }
.back-to-top.visible { opacity: 1; visibility: visible; }
.progress-ring-bg, .progress-ring-fg { fill: none; stroke-width: 5; }
.progress-ring-bg { stroke: rgba(255,255,255,0.3); }
.progress-ring-fg { stroke: white; stroke-dasharray: 283; stroke-dashoffset: 283; transform: rotate(-90deg); transform-origin: 50% 50%; transition: stroke-dashoffset 0.1s; }
.back-to-top span { position: absolute; }

/* --- LIGHTBOX --- */
.lightbox { position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.8); z-index: 3000; display: none; justify-content: center; align-items: center; }
.lightbox.visible { display: flex; }
.lightbox-content { position: relative; }
.lightbox-image { max-width: 90vw; max-height: 80vh; }
.lightbox-close, .lightbox-prev, .lightbox-next { position: absolute; background: none; border: none; color: white; font-size: 2rem; cursor: pointer; }
.lightbox-close { top: 20px; right: 20px; }
.lightbox-prev { top: 50%; left: 20px; transform: translateY(-50%); }
.lightbox-next { top: 50%; right: 20px; transform: translateY(-50%); }
.gallery-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: var(--spacing-md); }
.gallery-item img { width: 100%; height: 100%; object-fit: cover; border-radius: var(--radius-md); transition: transform 0.2s ease; }
.gallery-item:hover img { transform: scale(1.05); }

/* --- ANIMATIONS --- */
.reveal-fade { opacity: 0; transform: translateY(20px); transition: opacity 0.6s ease-out, transform 0.6s ease-out; }
.reveal-group .reveal-fade { transition-delay: calc(var(--reveal-order, 0) * 100ms); }
.revealed { opacity: 1; transform: translateY(0); }
@media (prefers-reduced-motion: reduce) {
    .reveal-fade { transition: opacity 0.6s ease-out; transform: none; }
    .button:hover, .service-card:hover { transform: none; }
    .sticky-header, .back-to-top, .sticky-cta { transition: none; }
    html { scroll-behavior: auto; }
}

/* --- UTILITIES --- */
.skip-link { position: absolute; top: -40px; left: 0; background: var(--primary); color: white; padding: 8px; z-index: 9999; transition: top 0.3s; }
.skip-link:focus { top: 0; }
.legal-text-section a { text-decoration: underline; }

/* --- RESPONSIVE --- */
@media (max-width: 992px) {
    .hero-container, .text-image-container, .text-image-container.image-left, .location-container, .contact-grid { grid-template-columns: 1fr; }
    .text-image-container.image-left .image-content { grid-row: 1; }
    .text-image-container.image-left .text-content { grid-row: 2; }
    .hero-image-wrapper { margin-top: var(--spacing-xl); }
    .header-cta { display: none; }
    .mobile-nav-toggle { display: block; }
    .main-nav { position: fixed; top: 0; right: -100%; width: 300px; height: 100vh; background: var(--bg); box-shadow: -5px 0 15px rgba(0,0,0,0.1); transition: right 0.4s ease-in-out; flex-direction: column; padding-top: 100px; }
    .main-nav.open { right: 0; }
    .main-nav ul { flex-direction: column; align-items: center; }
    .sticky-cta .container { flex-direction: column; gap: var(--spacing-sm); text-align: center; }
}

@media (max-width: 768px) {
    .footer-container { text-align: center; }
    .cookie-banner .container { flex-direction: column; text-align: center; }
}

document.addEventListener('DOMContentLoaded', () => {

    // --- 1. Mobile Navigation Toggle ---
    const navToggle = document.querySelector('.mobile-nav-toggle');
    const mainNav = document.querySelector('.main-nav');
    if (navToggle && mainNav) {
        navToggle.addEventListener('click', () => {
            const isExpanded = navToggle.getAttribute('aria-expanded') === 'true';
            navToggle.setAttribute('aria-expanded', !isExpanded);
            navToggle.classList.toggle('open');
            mainNav.classList.toggle('open');
            document.body.style.overflow = mainNav.classList.contains('open') ? 'hidden' : '';
        });
    }

    // --- 2. Sticky Header --- 
    const header = document.querySelector('.site-header');
    if (header) {
        window.addEventListener('scroll', () => {
            if (window.scrollY > 50) {
                header.classList.add('scrolled');
            } else {
                header.classList.remove('scrolled');
            }
        });
    }

    // --- 3. Scroll Reveal Animations ---
    const revealObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                if (entry.target.classList.contains('reveal-group')) {
                    const elements = entry.target.querySelectorAll('.reveal-fade');
                    elements.forEach((el, index) => {
                        el.style.setProperty('--reveal-order', index);
                        el.classList.add('revealed');
                    });
                } else {
                    entry.target.classList.add('revealed');
                }
                observer.unobserve(entry.target);
            }
        });
    }, { threshold: 0.1 });

    document.querySelectorAll('.reveal-fade, .reveal-group').forEach(el => {
        revealObserver.observe(el);
    });

    // --- 4. FAQ Accordion ---
    const faqItems = document.querySelectorAll('.faq-item');
    faqItems.forEach(item => {
        const question = item.querySelector('.faq-question');
        const answer = item.querySelector('.faq-answer');
        if (question && answer) {
            question.addEventListener('click', () => {
                const isExpanded = question.getAttribute('aria-expanded') === 'true';
                question.setAttribute('aria-expanded', !isExpanded);
                if (!isExpanded) {
                    answer.style.maxHeight = answer.scrollHeight + 'px';
                } else {
                    answer.style.maxHeight = '0';
                }
            });
        }
    });

    // --- 5. Carousel --- 
    const carousel = document.querySelector('.carousel-slides');
    if (carousel) {
        const slides = Array.from(carousel.children);
        const nextButton = document.querySelector('.carousel-button.next');
        const prevButton = document.querySelector('.carousel-button.prev');
        const dotsNav = document.querySelector('.carousel-dots');
        const slideWidth = slides[0].getBoundingClientRect().width;

        let currentIndex = 0;

        const createDots = () => {
            slides.forEach((_, i) => {
                const dot = document.createElement('button');
                dot.classList.add('dot');
                dot.addEventListener('click', () => moveToSlide(i));
                dotsNav.appendChild(dot);
            });
        };

        const updateDots = () => {
            const dots = Array.from(dotsNav.children);
            dots.forEach((dot, i) => {
                dot.classList.toggle('active', i === currentIndex);
            });
        };

        const moveToSlide = (targetIndex) => {
            carousel.scrollTo({ left: slides[targetIndex].offsetLeft, behavior: 'smooth' });
            currentIndex = targetIndex;
            updateDots();
        };

        nextButton.addEventListener('click', () => {
            const nextIndex = (currentIndex + 1) % slides.length;
            moveToSlide(nextIndex);
        });

        prevButton.addEventListener('click', () => {
            const prevIndex = (currentIndex - 1 + slides.length) % slides.length;
            moveToSlide(prevIndex);
        });

        createDots();
        updateDots();
    }

    // --- 6. Cookie Banner ---
    const cookieBanner = document.querySelector('.cookie-banner');
    const acceptButton = document.querySelector('.cookie-accept');
    if (cookieBanner && acceptButton) {
        if (!localStorage.getItem('cookiesAccepted')) {
            cookieBanner.classList.add('visible');
        }
        acceptButton.addEventListener('click', () => {
            localStorage.setItem('cookiesAccepted', 'true');
            cookieBanner.style.display = 'none';
        });
    }

    // --- 7. Sticky Context CTA ---
    const stickyCTA = document.querySelector('.sticky-cta');
    if (stickyCTA) {
        window.addEventListener('scroll', () => {
            const heroHeight = document.querySelector('.hero')?.offsetHeight || 600;
            if (window.scrollY > heroHeight) {
                stickyCTA.classList.add('visible');
            } else {
                stickyCTA.classList.remove('visible');
            }
        });
    }

    // --- 8. Back to Top Button with Progress Ring ---
    const backToTop = document.querySelector('.back-to-top');
    const progressRing = document.querySelector('.progress-ring-fg');
    if (backToTop && progressRing) {
        const radius = progressRing.r.baseVal.value;
        const circumference = 2 * Math.PI * radius;
        progressRing.style.strokeDasharray = `${circumference} ${circumference}`;
        progressRing.style.strokeDashoffset = circumference;

        window.addEventListener('scroll', () => {
            const scrollTop = window.scrollY;
            const docHeight = document.documentElement.scrollHeight - window.innerHeight;
            const scrollPercent = (scrollTop / docHeight) || 0;
            const offset = circumference - scrollPercent * circumference;
            progressRing.style.strokeDashoffset = offset;

            if (scrollTop > 300) {
                backToTop.classList.add('visible');
            } else {
                backToTop.classList.remove('visible');
            }
        });

        backToTop.addEventListener('click', () => {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        });
    }

    // --- 9. Lightbox Gallery ---
    const lightbox = document.getElementById('lightbox');
    const galleryItems = document.querySelectorAll('.gallery-item');
    if (lightbox && galleryItems.length > 0) {
        const lightboxImage = lightbox.querySelector('.lightbox-image');
        const closeButton = lightbox.querySelector('.lightbox-close');
        const prevButton = lightbox.querySelector('.lightbox-prev');
        const nextButton = lightbox.querySelector('.lightbox-next');
        let currentIndex = 0;

        const images = Array.from(galleryItems).map(item => ({
             src: item.href,
             alt: item.querySelector('img').alt
        }));

        function showImage(index) {
            const img = images[index];
            lightboxImage.src = img.src;
            lightboxImage.alt = img.alt;
            currentIndex = index;
        }

        galleryItems.forEach((item, index) => {
            item.addEventListener('click', e => {
                e.preventDefault();
                lightbox.classList.add('visible');
                showImage(index);
            });
        });

        closeButton.addEventListener('click', () => lightbox.classList.remove('visible'));
        prevButton.addEventListener('click', () => showImage((currentIndex - 1 + images.length) % images.length));
        nextButton.addEventListener('click', () => showImage((currentIndex + 1) % images.length));

        lightbox.addEventListener('click', e => {
            if (e.target === lightbox) lightbox.classList.remove('visible');
        });

        document.addEventListener('keydown', e => {
            if (lightbox.classList.contains('visible')) {
                if (e.key === 'Escape') lightbox.classList.remove('visible');
                if (e.key === 'ArrowLeft') prevButton.click();
                if (e.key === 'ArrowRight') nextButton.click();
            }
        });
    }
});
